  ),
  /* Constructing a dictionary of type AS_GSS_P_VendorData based on the data returned*/
  if(
    or(
      rule!AS_CO_UT_isBlank(local!vendorData.result),
      rule!AS_CO_UT_isBlank(
        rule!AS_GSS_UT_deepIndex(
          item: local!vendorData,
