    rule!AS_CO_UT_cast(
      type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_P_VendorData',
      value: {
        data: null,
        error: if(
          rule!AS_CO_UT_isBlank(local!vendorData.error),
          cons!AS_GSS_TXT_NO_DATA_RETURNED_FROM_SAM_GOV,
          local!vendorData.error
        )
      }
    ),
    /*   When data is retrieved */
    #"SYSTEM_SYSRULES_forEach"(
      items: local!vendorData.result.body.entityData,
      expression: /* Updating the vendor fields with data from the integration. */
      rule!AS_CO_UT_cast(
