            ),
            /*Get the updated eval vendor docs data*/
            local!incomingDoc: rule!AS_CO_UT_filterCdtByField(
              cdt: ri!updatedEvaluationVendorDocs,
              field: "evalDocumentId",
              value: fv!item
            ),
            if(
              /*If no original docs, soft delete newest docs*/
              rule!AS_CO_UT_isBlank(local!baseDoc),
                rule!AS_CO_UT_updateCdtFields(
                  cdt: local!incomingDoc,
                  fields: {"isDeleted"},
                  newValues: {true}
                ),
                /*Default, take the base/original values since they will be active and have unchanged submission date*/
                local!baseDoc
              )
            )
          )
        )
      )
    )
  )
)

_a-0000e5bc-4a9a-8000-9bbc-011c48011c48_.sail_code:
a!localVariables(
  local!includeAllDocTypesForUpload: a!defaultValue(ri!includeAllDocTypesForUpload, false()),
  local!gssPropertyLabels: rule!AS_GSS_CO_UT_loadBundleFromFolder(langISOCode: null),
  local!docTypes: rule!AS_GSS_QE_getRefDataByTypes(
    refTypes: cons!AS_GSS_REF_TYPE_DOCUMENT_TYPE,
    excludeRefDataIds: if(
      local!includeAllDocTypesForUpload,
      null,
      rule!AS_GSS_BL_documentTypesToExcludeFromSelectionOnUpload()
    )
  ),
  local!vendorDocuments,
  local!factorDocuments,
  local!consensusDocuments,
  local!otherDocuments,
  local!defaultFactorId: a!refreshVariable(
    value:
    rule!AS_GSS_BL_defaultFactorDocumentMappingCdt(
    evaluation: ri!evaluation,
    evaluationCriterias: ri!evaluationCriterias
  ),
  refreshOnVarChange:{
    ri!evaluation,ri!evaluationCriterias
  }
  ),
  #"SYSTEM_SYSRULES_formLayout_v1"(
    label: rule!AS_GAM_CO_I18N_UT_displayLabel(
      bundle: local!gssPropertyLabels,
      bundleKey: "lbl_UploadDocuments",
      arguments: if(
        rule!AS_CO_UT_isBlank(ri!evaluation.evaluationNumber),
        "",
        " | " & ri!evaluation.evaluationNumber
      )
    ),
    instructions: "Required fields are marked with an asterisk (*)",
    contents: choose(
      ri!currentStep,
      /*Upload doc*/
      rule!AS_GSS_CPS_uploadDocuments(
        evaluationDocuments: ri!evaluationDocuments,
        evaluation: ri!evaluation,
        i18nData: local!gssPropertyLabels,
        vendorId: ri!vendorId,
        refData: local!docTypes
      ),
      /*Select doc type*/
      rule!AS_GSS_GRD_selectDocTypeForUploadedDocs(
        evaluationDocuments: ri!evaluationDocuments,
        docTypes: local!docTypes,
        i18nData: local!gssPropertyLabels,
        vendorId: ri!vendorId,
        isUploadDoc: ri!isUploadDoc
      ),
      /*Associate docs with vendor / criteria*/
      rule!AS_GSS_GRD_associateDocuments(
        evaluationCriterias: ri!evaluationCriterias,
        evaluationVendors: ri!evaluationVendors,
        i18nData: local!gssPropertyLabels,
        vendorDocuments: local!vendorDocuments,
        factorDocuments: local!factorDocuments,
        consensusDocuments: local!consensusDocuments,
        vendorId: ri!vendorId,
        factorDocumentMapping: ri!factorDocumentMapping
      )
    ),
    buttons: rule!AS_CO_CPS_cancelSaveButtons(
      confirmLabel: rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundle: local!gssPropertyLabels,
        bundleKey: if(
          ri!currentStep = 1,
          "btn_Upload",
          if(
            ri!currentStep = 2,
            if(
              ri!isUploadDoc,
              /*As the automation script not able to find the Select button, using different
              bundle key when Automation toggle is ON*/
              if(
                cons!AS_GSS_TOGGLE_AUTOMATION_TESTING_ENABLED,
                "plc_DocumentType",
                "btn_Select"
              ),
              "btn_Confirm"
            ),
            "btn_Submit"
          )
        )
      ),
      cancelLabel: "Cancel",
      confirmSaveInto: {
        a!save(
          ri!userAction,
          if(
            ri!currentStep = 1,
            cons!AS_CO_ENUM_USER_ACTION_UPLOAD,
            cons!AS_CO_ENUM_USER_ACTION_SUBMIT
          )
        ),
        if(
          and(
            ri!currentStep = 2,
            or(
              contains(
                ri!evaluationDocuments.docType.refDataId,
                cons!AS_GSS_REF_ID_DOC_TYPE_VENDOR
              ),
              contains(
                ri!evaluationDocuments.docType.refDataId,
                cons!AS_GSS_REF_ID_DOC_TYPE_FACTOR
              ),
              contains(
                ri!evaluationDocuments.docType.refDataId,
                cons!AS_GSS_REF_ID_DOC_TYPE_CONSENSUS
              )
            )
          ),
          /*Grouping the docs based on doc type*/
          {
            a!save(
              local!vendorDocuments,
              rule!AS_CO_UT_filterCdtByField(
                cdt: ri!evaluationDocuments,
                field: "docType.refDataId",
                value: cons!AS_GSS_REF_ID_DOC_TYPE_VENDOR
              )
            ),
            if(
              rule!AS_CO_UT_isBlank(local!defaultFactorId),
              {},
              a!save(
                ri!factorDocumentMapping,
                append(
                  ri!factorDocumentMapping,
                  #"SYSTEM_SYSRULES_forEach"(
                    items: local!vendorDocuments,
                    expression: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_FactorDocumentMapping'(
                      document: fv!item,
                      factor: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria'(criteriaId: local!defaultFactorId),
                      createdBy: loggedInUser(),
                      createdDatetime: now(),
                      isActive: true
                    )
                  )
                )
              )
            ),
            a!save(
              local!factorDocuments,
              rule!AS_CO_UT_filterCdtByField(
                cdt: ri!evaluationDocuments,
                field: "docType.refDataId",
                value: cons!AS_GSS_REF_ID_DOC_TYPE_FACTOR
              )
            ),
            a!save(
              local!consensusDocuments,
              rule!AS_CO_UT_filterCdtByField(
                cdt: ri!evaluationDocuments,
                field: "docType.refDataId",
                value: cons!AS_GSS_REF_ID_DOC_TYPE_CONSENSUS
              )
            ),
            /*Saving Evaluator and Recommendation docs as other docs*/
            a!save(
              local!otherDocuments,
              index(
                ri!evaluationDocuments,
                wherecontains(
                  {
                    cons!AS_GSS_REF_ID_DOC_TYPE_EVALUATOR,
                    cons!AS_GSS_REF_ID_DOC_TYPE_RECOMMENDATION,
                    cons!AS_GSS_REF_ID_DOC_TYPE_REFERENCE
                  },
                  ri!evaluationDocuments.docType.refDataId
                ),
                null
              )
            )
          },
          if(
            ri!currentStep >= 3,
            a!save(
              ri!evaluationDocuments,
              {
                local!vendorDocuments,
                local!factorDocuments,
                local!consensusDocuments,
                local!otherDocuments
              }
            ),
            null
          )
        ),
        a!save(ri!currentStep, ri!currentStep + 1)
      },
      cancelSaveInto: a!save(
        ri!userAction,
        cons!AS_CO_ENUM_USER_ACTION_CANCEL
      ),
      confirmSubmit: if(
        /*when no vendor / factor / consensus documents are uploaded, the form will move to the third step instead of submitting*/
        and(
          ri!currentStep = 2,
          or(
            contains(
              ri!evaluationDocuments.docType.refDataId,
              cons!AS_GSS_REF_ID_DOC_TYPE_VENDOR
            ),
            contains(
              ri!evaluationDocuments.docType.refDataId,
              cons!AS_GSS_REF_ID_DOC_TYPE_FACTOR
            ),
            contains(
              ri!evaluationDocuments.docType.refDataId,
              cons!AS_GSS_REF_ID_DOC_TYPE_CONSENSUS
            )
          )
        ),
        false,
        true
      ),
      cancelSubmit: true,
      confirmAccessibilityText: if(
        ri!currentStep = 1,
        "Press Enter to upload file",
        if(
          ri!currentStep = 2,
          "Press Enter to select document type",
          "Press Enter to submit"
        )
      ),
      cancelAccessibilityText: "Press Enter to cancel the upload",
      cancelShowWhen: ri!currentStep = 1
    )
  )
)

