          item: rule!AS_CO_INP_textField(
            label: "Address Line 2",
            readOnly: ri!readOnly,
            value: ri!vendor.vendorAddressLine2,
            saveInto: ri!vendor.vendorAddressLine2,
            maxLength: cons!AS_CO_ENUM_TEXT_LENGTH_LONG
          )
        ),
        /* City */
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: rule!AS_CO_INP_textField(
            label: "City",
            readOnly: ri!readOnly,
            value: ri!vendor.vendorCity,
            saveInto: ri!vendor.vendorCity,
            maxLength: cons!AS_CO_ENUM_TEXT_LENGTH_MEDIUM
          )
        )
      }
    ),
    #"SYSTEM_SYSRULES_sideBySideLayout"(
      items: {
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: rule!AS_GSS_CP_countryField(
            countryValue: ri!vendor.vendorCountry,
            i18nData: ri!i18nData,
            readOnly: ri!readOnly,

          )
        ),
        /* If the Country is US, only then we will display State. */
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: rule!AS_GSS_CP_stateField(
            label: "State",
            readOnly: ri!readOnly,
            value: ri!vendor.vendorState,
            saveInto: ri!vendor.vendorState,
            placeholderLabel: "Select a state"
          ),
          showWhen: ri!vendor.vendorCountry.countryId = cons!AS_GAM_REF_ID_COUNTRY_UNITED_STATES
        ),
        /* If the Country is US, It is ZipCode Else Foreign Postal */
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: rule!AS_CO_INP_textField(
            label: if(
              ri!vendor.vendorCountry.countryId = cons!AS_GAM_REF_ID_COUNTRY_UNITED_STATES,
              "Zip Code",
              "Foreign Postal",

            ),
            readOnly: ri!readOnly,
            value: if(
              ri!vendor.vendorCountry.countryId = cons!AS_GAM_REF_ID_COUNTRY_UNITED_STATES,
              ri!vendor.vendorZipCode,
              ri!vendor.vendorForeignPostalCode
            ),
            saveInto: if(
              ri!vendor.vendorCountry.countryId = cons!AS_GAM_REF_ID_COUNTRY_UNITED_STATES,
              ri!vendor.vendorZipCode,
              ri!vendor.vendorForeignPostalCode
            ),
            maxLength: cons!AS_CO_ENUM_TEXT_LENGTH_SHORT
          )
        ),
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: rule!AS_CO_INP_textField(
            label: "Zip Code Ext",
            readOnly: ri!readOnly,
            value: ri!vendor.vendorZipCodeExt,
            saveInto: ri!vendor.vendorZipCodeExt,
            maxLength: cons!AS_CO_ENUM_TEXT_LENGTH_SHORT
          ),
          showWhen: ri!vendor.vendorCountry.countryId = cons!AS_GAM_REF_ID_COUNTRY_UNITED_STATES
        ),
        /*Additonal SBS Items For proper Alignment if the Country is Not US*/
        #"SYSTEM_SYSRULES_sideBySideItem"(
          showWhen: ri!vendor.vendorCountry.countryId <> cons!AS_GAM_REF_ID_COUNTRY_UNITED_STATES
        )
      }
    ),

  }
)

