rule!AS_CO_UT_captureMetadata(
  cdt: rule!AS_CO_UT_updateCdtFields(
    cdt: ri!vendor,
    fields: {
      "vendorZipCode",
      "vendorZipCodeExt",
      "vendorForeignPostalCode",
      "vendorState",
      "sourceApplicationId"
    },
    newValues: {
      if(
        tointeger(ri!vendor.vendorCountry.countryId) = cons!AS_GAM_REF_ID_COUNTRY_UNITED_STATES,
        ri!vendor.vendorZipCode,
        null
      ),
      if(
