a!localVariables(
  local!lenOfSearchTerm: len(ri!searchTerm),
  if(
    rule!AS_CO_UT_isBlank(ri!searchTerm),
    null,
    if(
      or(
        /*minimum length*/
        local!lenOfSearchTerm < min(
          {
            cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_CAGE,
            cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_UEI
          }
        ),
        /*In between*/
        and(
          local!lenOfSearchTerm > cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_CAGE,
          local!lenOfSearchTerm < cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_UEI
        ),
        /*Over the max*/
        local!lenOfSearchTerm > cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_UEI
      ),
      rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundle: ri!bundle,
        bundleKey: "vld_UeiOrCageCharLimit",
        arguments: if(
          ri!isSearchFromVendorsTab = true,
          "Search Vendor UEI or CAGE",
          "Search Vendors"
        )
      ),
      if(
        and(
          rule!AS_CO_UT_isBlank(ri!cage),
          rule!AS_CO_UT_isBlank(ri!uniqueEntityId)
        ),
        null,
        if(
          rule!AS_CO_UT_isNotBlank(ri!cage),
          /* CAGE validation */
          if(
            rule!AS_GAM_UT_validateAlphanumeric(ri!cage),
            null,
            rule!AS_GAM_CO_I18N_UT_displayLabel(
              bundle: ri!bundle,
              bundleKey: "vld_LabelAndAlphanumericValue",
              arguments: {
                "CAGE",
                cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_CAGE
              }
            ),

          ),
          /* UEI validation */
          if(
            rule!AS_GAM_UT_validateAlphanumeric(ri!uniqueEntityId),
            null,
            rule!AS_GAM_CO_I18N_UT_displayLabel(
              bundle: ri!bundle,
              bundleKey: "vld_LabelAndAlphanumericValue",
              arguments: {
                "Unique Entity ID",
                cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_UEI
              }
            ),

          )
        )
      )
    )
  )
)

