_a-0000e5da-a251-8000-9bbe-011c48011c48_.sail_code:
a!localVariables(
  /* Docs uploaded in previous session */
  local!activeDocs: a!refreshVariable(
    value: rule!AS_CO_UT_rejectNullValuesFromArray(
      rule!AS_CO_UT_filterCdtByField(
        cdt: ri!vendorDocs,
        field: "isDeleted",
        value: false
      )
    ),
    refreshOnReferencedVarChange: false
  ),
  /* Docs currently in the fileUploadComponent, so doesn't include removed docs in current session */
  /*ri!docsUploadedValue,*/
  /* Docs uploaded in the current session, including removed docs in current session */
  local!allCurrentlyUploadedDocIds,
  local!allVendorDocuments: rule!AS_CO_UT_rejectNullValuesFromArray(
    union(
      tointeger(local!activeDocs.appianDocId),
      tointeger(local!allCurrentlyUploadedDocIds)
    )
  ),
  local!documentsToDeactivate: difference(
    tointeger(local!allCurrentlyUploadedDocIds),
    tointeger(ri!docsUploadedValue)
  ),
  local!constructedUploadedVendorDocs: a!refreshVariable(
    value: local!activeDocs,
    refreshOnReferencedVarChange: false
  ),
  local!uploadedVendorDocs: if(
    rule!AS_CO_UT_isBlank(local!allVendorDocuments),
    {},
    rule!AS_GSS_QE_getEvaluationDocuments(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      evaluationId: ri!evaluationId,
      filters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: "appianDocId",
          operator: "in",
          value: local!allVendorDocuments
        )
      },
      triggerRefresh: ri!triggerRefresh
    ),

  ),
  {
    #"SYSTEM_SYSRULES_sectionLayout_v1"(
      label: "Vendor Evaluation Documents",
      labelColor: "STANDARD",
      labelHeadingTag: "H2",
      labelSize: "MEDIUM_PLUS",
      contents: {
        rule!AS_CO_CP_fileUploadField(
          labelPosition: "COLLAPSED",
          value: ri!docsUploadedValue,
          saveInto: {
            ri!docsUploadedValue,
            a!save(ri!docsUploaded, ri!docsUploadedValue),
            a!save(
              local!allCurrentlyUploadedDocIds,
              append(
                local!allCurrentlyUploadedDocIds,
                #"SYSTEM_SYSRULES_forEach"(
                  items: ri!docsUploadedValue,
                  expression: if(
                    contains(
                      tointeger(local!allCurrentlyUploadedDocIds),
                      tointeger(fv!item)
                    ),
                    {},
                    tointeger(fv!item)
                  )
                )
              )
            ),
            a!save(
              local!allVendorDocuments,
              rule!AS_CO_UT_rejectNullValuesFromArray(
                union(
                  tointeger(
                    index(local!activeDocs, "appianDocId", {})
                  ),
                  tointeger(local!allCurrentlyUploadedDocIds)
                )
              )
            ),
            a!save(
              local!constructedUploadedVendorDocs,
              #"SYSTEM_SYSRULES_forEach"(
                items: local!allVendorDocuments,
                expression: a!localVariables(
                  local!currentConstruct: rule!AS_CO_UT_displayValue(
                    value: fv!item,
                    inArray: local!constructedUploadedVendorDocs.appianDocId,
                    replacement: local!constructedUploadedVendorDocs,
                    customDefault: null
                  ),
                  if(
                    rule!AS_CO_UT_isBlank(local!currentConstruct),
                    'type!{urn:com:appian:types:AS:GSS}AS_GSS_EvaluationDocument'(
                      appianDocId: fv!item,
                      evaluationId: ri!evaluationId,
                      sourceApplication: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_R_Data'(
                        refDataId: cons!AS_GSS_REF_ID_SOURCE_APPLICATION_GSS
                      ),
                      isDeleted: false
                    ),
                    local!currentConstruct
                  )
                )
              )
            ),
            a!save(
              local!documentsToDeactivate,
              difference(
                tointeger(local!allCurrentlyUploadedDocIds),
                tointeger(ri!docsUploadedValue)
              )
            ),
            if(
              rule!AS_CO_UT_isBlank(local!documentsToDeactivate),
              {},
              a!save(
                local!constructedUploadedVendorDocs,
                rule!AS_CO_UT_updateCdtFields(
                  cdt: local!constructedUploadedVendorDocs,
                  fields: { "isDeleted" },
                  newValues: { true },
                  indices: wherecontains(
                    local!documentsToDeactivate,
                    tointeger(
                      index(
                        local!constructedUploadedVendorDocs,
                        "appianDocId",
                        {}
                      )
                    )
                  )
                )
              )
            ),
            #"SYSTEM_SYSRULES_writeToDataStoreEntity"(
              dataStoreEntity: cons!AS_GSS_ENT_EVALUATION_DOCUMENT,
              valueToStore: local!constructedUploadedVendorDocs,
              onSuccess: {
                a!save(
                  local!constructedUploadedVendorDocs,
                  a!flatten(fv!storedValues)
                )
              }
            ),
            a!save(
              local!allVendorDocuments,
              rule!AS_CO_UT_rejectNullValuesFromArray(
                union(
                  tointeger(
                    index(local!activeDocs, "appianDocId", {})
                  ),
                  tointeger(local!allCurrentlyUploadedDocIds)
                )
              )
            ),
            a!save(
              ri!vendorDocs,
              a!flatten(
                {
                  local!constructedUploadedVendorDocs,
                  if(
                    rule!AS_CO_UT_isBlank(ri!vendorDocs),
                    {},
                    ri!vendorDocs[wherecontains(
                      difference(
                        index(ri!vendorDocs, "evalDocumentId", {}), index(
                          local!constructedUploadedVendorDocs, "evalDocumentId", {}
                        )
                      ), index(ri!vendorDocs, "evalDocumentId", {})
                    )]
                  )
                }
              )
            ),
            a!save(
              ri!factorDocumentMapping,
              remove(
                ri!factorDocumentMapping,
                wherecontains(
                  local!documentsToDeactivate,
                  tointeger(
                    rule!AS_CO_UT_indexPath(
                      cdt: ri!factorDocumentMapping,
                      field: "document.appianDocId",
                      indexDefault: {}
                    )
                  )
                )
              )
            ),
            a!save(ri!triggerRefresh, now())
          },
          target: cons!AS_GSS_EVALUATION_DOCUMENT_FOLDERS,
          maxSelections: cons!AS_GSS_MAX_SELECTION_FOR_UPLOAD_DOC_RA,
          allowedFileExtensions: cons!AS_GSS_TXT_VALID_FILE_TYPES_FOR_EVALUATION_DOCUMENT,
          instructions: rule!AS_GSS_TXT_documentUploadInstructions(
            bundle: ri!i18nData,
            showAllowedFileTypes: true(),
            showMaxDocumentLimit: true(),
            maxDocumentsAllowed: cons!AS_GSS_MAX_SELECTION_FOR_UPLOAD_DOC_RA
          )
        )
      }
    ),
    #"SYSTEM_SYSRULES_richTextDisplayField"(
      labelPosition: "COLLAPSED",
      value: {
        if(
          rule!AS_CO_UT_isBlank(local!activeDocs),
          {},
          #"SYSTEM_SYSRULES_forEach"(
            items: local!activeDocs,
            expression: {
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: fv!item.documentName,
                    style: { "PLAIN" }
                  ),
                  " ",
                  #"SYSTEM_SYSRULES_richTextIcon"(
                    icon: "trash-o",
                    altText: "Press enter to delete the document",

                  ),
                  char(10)
                },
                color: "ACCENT",
                link: #"SYSTEM_SYSRULES_dynamicLink"(
                  value: true,
                  saveInto: {
                    a!save(
                      local!constructedUploadedVendorDocs,
                      rule!AS_CO_UT_updateCdtFields(
                        cdt: local!constructedUploadedVendorDocs,
                        indices: wherecontains(
                          tointeger(fv!item.evalDocumentId),
                          local!constructedUploadedVendorDocs.evalDocumentId
                        ),
                        fields: "isDeleted",
                        newValues: true,

                      )
                    ),
                    a!save(
                      ri!vendorDocs,
                      rule!AS_CO_UT_updateCdtFields(
                        cdt: ri!vendorDocs,
                        indices: wherecontains(
                          tointeger(fv!item.evalDocumentId),
                          ri!vendorDocs.evalDocumentId
                        ),
                        fields: "isDeleted",
                        newValues: true,

                      )
                    ),
                    #"SYSTEM_SYSRULES_writeToDataStoreEntity"(
                      dataStoreEntity: cons!AS_GSS_ENT_EVALUATION_DOCUMENT,
                      valueToStore: local!constructedUploadedVendorDocs,
                      onSuccess: {
                        a!save(
                          local!constructedUploadedVendorDocs,
                          a!flatten(fv!storedValues)
                        )
                      }
                    ),
                    a!save(
                      ri!factorDocumentMapping,
                      rule!AS_CO_UT_updateCdtFields(
                        cdt: ri!factorDocumentMapping,
                        fields: "isActive",
                        newValues: false,
                        indices: wherecontains(
                          fv!item.evalDocumentId,
                          tointeger(
                            rule!AS_CO_UT_indexPath(
                              cdt: ri!factorDocumentMapping,
                              field: "document.evalDocumentId",
                              indexDefault: {}
                            )
                          )
                        )
                      )
                    ),
                    a!save(
                      local!activeDocs,
                      remove(local!activeDocs, fv!index)
                    ),
                    a!save(ri!triggerRefresh, now())
                  }
                ),
                linkStyle: "STANDALONE"
              )
            }
          )
        )
      },
      showWhen: or(
        rule!AS_CO_UT_isNotBlank(ri!docsUploaded),
        rule!AS_CO_UT_isNotBlank(local!activeDocs)
      )
    ),
    rule!AS_GSS_GRD_factorSelectionToEvalVendorDocsWizard(
      vendorDocuments: local!uploadedVendorDocs,
      factorDocumentMapping: ri!factorDocumentMapping,
      evaluationId: ri!evaluationId,
      i18nData: ri!i18nData,
      uploadValue: index(
        local!uploadedVendorDocs,
        "appianDocId",
        null
      ),
      criteria: ri!criteria,
      existingFactorDocumentMappings: ri!existingFactorDocumentMappings
    )
  }
)

