a!localVariables(
  local!adminActions: rule!AS_GSS_TMG_BL_taskManagementOptions(),
  local!adminSubActions: a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      items: local!adminActions,
      expression: fv!item.subActions
    )
  ),
  local!selectedSubActionId: if(
    or(
      isnull(ri!selectedSubActionId),
      ri!selectedSubActionId = 0
    ),
    local!adminSubActions[1].subActionId,
    ri!selectedSubActionId
  ),
  local!selectedSubAction: rule!AS_CO_UT_displayValue(
    value: local!selectedSubActionId,
    inArray: local!adminSubActions.subActionId,
    replacement: local!adminSubActions
  ),
  local!i18nData: rule!AS_GSS_CO_UT_loadBundleFromFolder(langISOCode: null),
  rule!AS_GSS_HCL_displayWrapperContents(
    contents: #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            rule!AS_GSS_CPS_cardsAsSectionedSideNavigation(
              choices: #"SYSTEM_SYSRULES_forEach"(
                items: local!adminActions,
                expression: {
                  label: fv!item.label,
                  choiceLabels: fv!item.subActions.label,
                  choiceValues: fv!item.subActions.subActionId,
                  choiceLinks: #"SYSTEM_SYSRULES_forEach"(
                    items: fv!item.subActions,
                    expression: #"SYSTEM_SYSRULES_startProcessLink"(
                      label: if(
                        cons!AS_GSS_TOGGLE_AUTOMATION_TESTING_ENABLED,
                        fv!item.label,
                        ""
                      ),
                      processModel: cons!AS_GSS_TMG_PM_TASK_MANAGEMENT,
                      processParameters: {
                        selectedSubActionId: fv!item.subActionId
                      }
                    )
                  )
                }
              ),
              value: local!selectedSubActionId,
              saveInto: local!selectedSubActionId,
              i18nData: local!i18nData
            )
          },
          width: "NARROW"
        ),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: local!selectedSubAction.interface()
        )
      }
    ),
    backgroundColor: rule!AS_GSS_BrandingValueByKey(
      brandingKey: "DefaultBackground",
      useSuiteBranding: true
    )
  )
)

