a!localVariables(
  local!bundle: rule!AS_GSS_CO_UT_loadBundleFromFolder(langISOCode: null),
  local!triggerRefresh: a!refreshVariable(
    value: now(),
    refreshInterval: 0.5,
    refreshOnVarChange: ri!backToDataViewCounter
  ),
  local!isStateOrLocal: rule!AS_GSS_EnableOrDisableStateAndLocalGovernment_wrapper(),
  local!vendorCountsBasedOnStatus: a!refreshVariable(
    value: rule!AS_GSS_QR_getReferenceVendors(
      fetchAll: true(),
      executeWhen: true(),
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_AGGREGATION,
      aggregationFields: {
        #"SYSTEM_SYSRULES_aggregationFields"(
          groupings: if(
            local!isStateOrLocal,
            {
              #"SYSTEM_SYSRULES_grouping"(
                field: recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.reconciliationMigrationStatusForSLG,
                alias: "reconciliationMigrationStatus"
              )
            },
            {
              #"SYSTEM_SYSRULES_grouping"(
                field: recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.reconciliationMigrationStatusForFederal,
                alias: "reconciliationMigrationStatus"
              )
            }
          ),
          measures: #"SYSTEM_SYSRULES_measure"(
            field: recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.vendorRefId,
            alias: "totalCount",
            function: "COUNT"
          )
        ),

      },

    ),
    refreshAfter: "RECORD_ACTION"
  ),
  local!latestReconciliation: rule!AS_GSS_DRM_QR_getReconciliationRequests(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    additionalFilters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!AS_GSS_DRM_ReconciliationDetails_SYNCEDRECORD.isActive,
        operator: "=",
        value: true()
      )
    },
    sortInfo: #"SYSTEM_SYSRULES_sortInfo"(
      field: recordType!AS_GSS_DRM_ReconciliationDetails_SYNCEDRECORD.reconciliationId,
      ascending: false()
    ),
    triggerRefresh: local!triggerRefresh
  ),
  local!latestMigration: rule!AS_GSS_DRM_QR_getMigrationDetails(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    additionalFilters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!AS_GSS_DRM_MigrationDetails_SYNCEDRECORD.isActive,
        operator: "=",
        value: true()
      )
    },
    sortInfo: #"SYSTEM_SYSRULES_sortInfo"(
      field: recordType!AS_GSS_DRM_MigrationDetails_SYNCEDRECORD.migrationId,
      ascending: false()
    ),
    triggerRefresh: local!triggerRefresh
  ),
  local!reconciliationResponse: a!refreshVariable(
    value: if(
      rule!AS_CO_UT_isBlank(
        local!latestReconciliation[recordType!AS_GSS_DRM_ReconciliationDetails_SYNCEDRECORD.gsmReconciliationId]
      ),
      null,
      rule!AS_CO_UT_indexPath(
        cdt: rule!AS_GSS_DRM_INT_GET_DataReconciliationStatus(
          reconcilitationId: local!latestReconciliation[recordType!AS_GSS_DRM_ReconciliationDetails_SYNCEDRECORD.gsmReconciliationId]
        ),
        field: "result",
        indexDefault: {}
      )
    ),
    refreshOnVarChange: local!triggerRefresh
  ),
  local!metadata: rule!AS_GSS_DRM_constructMetaData(
    latestMigration: local!latestMigration,
    latestReconciliation: local!latestReconciliation,
    reconciliationResponse: local!reconciliationResponse,
    isLastStep: ri!isLastStep
  ),
  local!steps: {
    "Data View",
    "Data Reconciliation",
    "Data Migration",

  },
  local!activeStep: local!metadata.activeStep,
  #"SYSTEM_SYSRULES_formLayout_v1"(
    backgroundColor: "#FAFAFC",
    showButtonFooterDivider: false(),
    contents: {
      #"SYSTEM_SYSRULES_milestoneField"(
        label: "",
        labelPosition: "COLLAPSED",
        steps: local!steps,
        active: local!activeStep,
        stepStyle: "LINE",
        orientation: "HORIZONTAL",
        color: "ACCENT",
        marginAbove: "MORE",
        marginBelow: "MORE"
      ),
      choose(
        local!activeStep,
        rule!AS_GSS_DRM_SEC_dataView(
          vendorCountsBasedOnStatus: local!vendorCountsBasedOnStatus,
          backToDataViewCounter: ri!backToDataViewCounter
        ),
        rule!AS_GSS_DRM_SEC_dataReconciliation(
          bundle: local!bundle,
          isReconFailed: local!metadata.isReconFailed,
          reconciliationResponse: local!reconciliationResponse,
          latestReconciliation: local!latestReconciliation,
          isReconCompleted: local!metadata.isReconCompleted,
          isStateOrLocal: local!isStateOrLocal
        ),
        rule!AS_GSS_DRM_SEC_dataMigration(
          bundle: local!bundle,
          isMigrationInProgress: local!metadata.isMigrationInProgress
        ),
        {}
      )
    },
    buttons: #"SYSTEM_SYSRULES_ButtonArrayLayout"(
      buttons: {
        /*Start Data Migration button*/
        #"SYSTEM_SYSRULES_buttonWidget_v1"(
          label: "Start Data Reconciliation",
          style: "SOLID",
          saveInto: {
            a!save(local!activeStep, 2),
            #"SYSTEM_SYSRULES_startProcess3"(
              processModel: cons!AS_GSS_DRM_PM_START_RECONCILIATION,
              /*isSynchronous: true(),*/
              onSuccess: {
                a!save(
                  local!latestReconciliation,
                  rule!AS_GSS_DRM_QR_getReconciliationRequests(
                    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
                    additionalFilters: {
                      #"SYSTEM_SYSRULES_queryFilter"(
                        field: recordType!AS_GSS_DRM_ReconciliationDetails_SYNCEDRECORD.isActive,
                        operator: "=",
                        value: true()
                      )
                    },
                    sortInfo: #"SYSTEM_SYSRULES_sortInfo"(
                      field: recordType!AS_GSS_DRM_ReconciliationDetails_SYNCEDRECORD.reconciliationId,
                      ascending: false()
                    )
                  )
                )
              }
            )
          },
          showWhen: local!activeStep = 1,
          disabled: or(
            local!metadata.isReconInProgress,
            a!isNullOrEmpty(local!vendorCountsBasedOnStatus),
            rule!AS_CO_UT_indexWhere(
              value: 2,
              initialArray: index(
                local!vendorCountsBasedOnStatus,
                "reconciliationMigrationStatus",
                null
              ),
              finalArray: index(
                local!vendorCountsBasedOnStatus,
                "totalCount",
                null
              )
            ) < 1
          )
        ),
        /*Start Data Migration button*/
        #"SYSTEM_SYSRULES_buttonWidget_v1"(
          label: "Start Data Migration",
          style: "SOLID",
          saveInto: {
            a!save(local!activeStep, 3),
            a!save(ri!isLastStep, true),
            #"SYSTEM_SYSRULES_startProcess3"(
              processModel: cons!AS_GSS_DRM_PM_START_DATA_MIGRATION,
              /*isSynchronous: true(),*/
              onSuccess: {
                a!save(
                  local!latestMigration,
                  rule!AS_GSS_DRM_QR_getMigrationDetails(
                    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
                    additionalFilters: {
                      #"SYSTEM_SYSRULES_queryFilter"(
                        field: recordType!AS_GSS_DRM_MigrationDetails_SYNCEDRECORD.isActive,
                        operator: "=",
                        value: true()
                      )
                    },
                    sortInfo: #"SYSTEM_SYSRULES_sortInfo"(
                      field: recordType!AS_GSS_DRM_MigrationDetails_SYNCEDRECORD.migrationId,
                      ascending: false()
                    )
                  )
                )
              }
            )
          },
          showWhen: and(
            local!activeStep = 2,
            not(local!metadata.isReconFailed)
          ),
          disabled: or(
            local!metadata.isReconInProgress,
            local!metadata.isMigrationInProgress
          )
        ),
        /*Back to Data View*/
        #"SYSTEM_SYSRULES_buttonWidget_v1"(
          label: "Back To Data View",
          style: "SOLID",
          saveInto: {
            a!save(local!activeStep, 1),
            a!save(
              ri!backToDataViewCounter,
              ri!backToDataViewCounter + 1
            ),
            a!save(
              local!vendorCountsBasedOnStatus,
              rule!AS_GSS_QR_getReferenceVendors(
                fetchAll: true(),
                executeWhen: true(),
                returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_AGGREGATION,
                aggregationFields: {
                  #"SYSTEM_SYSRULES_aggregationFields"(
                    groupings: if(
                      local!isStateOrLocal,
                      {
                        #"SYSTEM_SYSRULES_grouping"(
                          field: recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.reconciliationMigrationStatusForSLG,
                          alias: "reconciliationMigrationStatus"
                        )
                      },
                      {
                        #"SYSTEM_SYSRULES_grouping"(
                          field: recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.reconciliationMigrationStatusForFederal,
                          alias: "reconciliationMigrationStatus"
                        )
                      }
                    ),
                    measures: #"SYSTEM_SYSRULES_measure"(
                      field: recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.vendorRefId,
                      alias: "totalCount",
                      function: "COUNT"
                    )
                  ),

                },

              )
            )
          },
          showWhen: and(
            local!activeStep = 3,
            local!metadata.isMigrationCompleted
          )
        )
      }
    ),
    showWhen: not(ri!isHomePage)
  )
)

