a!localVariables(
  local!isReconInProgress: or(
    tointeger(
      rule!AS_CO_UT_indexPath(
        cdt: ri!reconciliationResponse,
        field: "statusCode",
        indexDefault: {}
      )
    ) = cons!AS_GSS_DRM_INT_DATA_RECONCILIATION_ERROR_CODE_202,
    tointeger(
      ri!latestReconciliation[recordType!AS_GSS_DRM_ReconciliationDetails_SYNCEDRECORD.status]
    ) = cons!AS_GSS_DRM_INT_DATA_RECONCILIATION_ERROR_CODE_202,
    and(
      tointeger(
        ri!latestReconciliation[recordType!AS_GSS_DRM_ReconciliationDetails_SYNCEDRECORD.totalIterationCount]
      ) <> tointeger(
        ri!latestReconciliation[recordType!AS_GSS_DRM_ReconciliationDetails_SYNCEDRECORD.currentIterationCount]
      ),
      tointeger(
        rule!AS_CO_UT_indexPath(
          cdt: ri!reconciliationResponse,
          field: "statusCode",
          indexDefault: {}
        )
      ) = cons!AS_GSS_DRM_INT_DATA_RECONCILIATION_ERROR_CODE_200
    )
  ),
  local!isReconCompleted: and(
    tointeger(
      ri!latestReconciliation[recordType!AS_GSS_DRM_ReconciliationDetails_SYNCEDRECORD.status]
    ) = cons!AS_GSS_DRM_INT_DATA_RECONCILIATION_ERROR_CODE_200,
    tointeger(
      rule!AS_CO_UT_indexPath(
        cdt: ri!reconciliationResponse,
        field: "statusCode",
        indexDefault: {}
      )
    ) = cons!AS_GSS_DRM_INT_DATA_RECONCILIATION_ERROR_CODE_200,
    tointeger(
      ri!latestReconciliation[recordType!AS_GSS_DRM_ReconciliationDetails_SYNCEDRECORD.totalIterationCount]
    ) = tointeger(
      ri!latestReconciliation[recordType!AS_GSS_DRM_ReconciliationDetails_SYNCEDRECORD.currentIterationCount]
    )
  ),
  local!isReconFailed: and(
    a!isNotNullOrEmpty(ri!reconciliationResponse),
    not(
      contains(
        {
          cons!AS_GSS_DRM_INT_DATA_RECONCILIATION_ERROR_CODE_200,
          cons!AS_GSS_DRM_INT_DATA_RECONCILIATION_ERROR_CODE_202
        },
        tointeger(
          rule!AS_CO_UT_indexPath(
            cdt: ri!reconciliationResponse,
            field: "statusCode",
            indexDefault: {}
          )
        )
      )
    )
  ),
  local!isMigrationInProgress: tointeger(
    ri!latestMigration[recordType!AS_GSS_DRM_MigrationDetails_SYNCEDRECORD.statusId]
  ) = cons!AS_GSS_DRM_INT_REF_ID_MIGRATION_PROCESS_STATUS_IN_PROGRESS,
  local!isMigrationCompleted: tointeger(
    ri!latestMigration[recordType!AS_GSS_DRM_MigrationDetails_SYNCEDRECORD.statusId]
  ) = cons!AS_GSS_DRM_INT_REF_ID_MIGRATION_PROCESS_STATUS_COMPLETED,
  a!map(
    isReconInProgress: local!isReconInProgress,
    isReconFailed: local!isReconFailed,
    isReconCompleted: local!isReconCompleted,
    isMigrationInProgress: local!isMigrationInProgress,
    isMigrationCompleted: local!isMigrationCompleted,
    activeStep: a!match(
      value: 1,
      whenTrue: and(
        local!isReconCompleted,
        local!isMigrationCompleted,
        ri!isLastStep
      ),
      then: 3,
      whenTrue: and(
        local!isReconCompleted,
        local!isMigrationCompleted,
        not(ri!isLastStep)
      ),
      then: 1,
      whenTrue: local!isMigrationInProgress,
      then: 3,
      whenTrue: or(
        local!isReconInProgress,
        local!isReconFailed,
        and(
          local!isReconCompleted,
          rule!AS_CO_UT_isBlank(
            value: ri!latestMigration[recordType!AS_GSS_DRM_MigrationDetails_SYNCEDRECORD.migrationId]
          )
        )
      ),
      then: 2,
      default: 1
    )
  )
)

