        #"SYSTEM_SYSRULES_columnLayout"(width: "EXTRA_NARROW"),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            #"SYSTEM_SYSRULES_columnsLayout"(
              columns: {
                #"SYSTEM_SYSRULES_columnLayout"(
                  contents: {
                    #"SYSTEM_SYSRULES_recordActionField_v1"(
                      actions: {
                        #"SYSTEM_SYSRULES_recordActionItem"(
                          action: #"urn:appian:record-action:v1:bde9fe6b-9c88-4aff-9758-f9b0d2f4df6a/703805d3-cef1-441d-9220-9a2a387f846a"
                        ),
                        #"SYSTEM_SYSRULES_recordActionItem"(
                          action: #"urn:appian:record-action:v1:bde9fe6b-9c88-4aff-9758-f9b0d2f4df6a/8aaf7258-6c78-4723-ab17-36aa39327666"
                        )
                      },
                      style: "TOOLBAR_PRIMARY",
                      display: "LABEL_AND_ICON"
                    )
                  }
                )
              },
              alignVertical: "TOP"
            )
          }
        ),
        #"SYSTEM_SYSRULES_columnLayout"(width: "EXTRA_NARROW")
      },
      alignVertical: "MIDDLE",
      marginAbove: "MORE",

    )
  },
  style: rule!AS_GSS_BrandingValueByKey(
    brandingKey: "BackgroundColor",
    useSuiteBranding: true
  ),
  marginAbove: "NONE",
  marginBelow: "NONE",
  showBorder: false,
  showWhen: rule!AS_CO_UT_booleanDefaultFalse(boolean: ri!showWhen)
)

#"SYSTEM_SYSRULES_cardLayout"(
  contents: {
    #"SYSTEM_SYSRULES_richTextDisplayField"(
      labelPosition: "COLLAPSED",
      value: {
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: {
            #"SYSTEM_SYSRULES_richTextIcon"(
              icon: "angle-left-bold",
              altText: "Start Page",
              caption: "Start Page",
              link: #"SYSTEM_SYSRULES_dynamicLink"(
                saveInto: a!save(
                  ri!pagingInfo,
                  #"SYSTEM_SYSRULES_pagingInfo"(
                    startIndex: 1,
                    batchSize: ri!pagingInfo.batchSize,
                    sort: ri!pagingInfo.sort
                  )
                ),
                showWhen: ri!pagingInfo.startIndex > 1
              ),
              linkStyle: "STANDALONE"
            ),
            #"SYSTEM_SYSRULES_richTextIcon"(
              icon: "angle-left",
              altText: "Previous Page",
              caption: "Previous Page",
              link: #"SYSTEM_SYSRULES_dynamicLink"(
                saveInto: a!save(
                  ri!pagingInfo,
                  #"SYSTEM_SYSRULES_pagingInfo"(
                    startIndex: ri!pagingInfo.startIndex - ri!pagingInfo.batchSize,
                    batchSize: ri!pagingInfo.batchSize,
                    sort: ri!pagingInfo.sort
                  )
                ),
                showWhen: ri!pagingInfo.startIndex > 1
              ),
              linkStyle: "STANDALONE"
            )
          },
          color: if(
            ri!pagingInfo.startIndex = 1,
            "SECONDARY",
            "ACCENT"
          ),
          style: { "STRONG" }
        ),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: {
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: {
                ri!pagingInfo.startIndex,
                "-",
                ri!pagingInfo.startIndex + length(ri!dataForCurrentPage) - 1
              },
              style: { "STRONG" }
            ),
            " of " & ri!totalCount
          },
          color: "STANDARD"
        ),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: {
            #"SYSTEM_SYSRULES_richTextIcon"(
              icon: "angle-right",
              altText: "Next Page",
              caption: "Next Page",
              link: #"SYSTEM_SYSRULES_dynamicLink"(
                saveInto: a!save(
                  ri!pagingInfo,
                  #"SYSTEM_SYSRULES_pagingInfo"(
                    startIndex: ri!pagingInfo.startIndex + ri!pagingInfo.batchSize,
                    batchSize: ri!pagingInfo.batchSize,
                    sort: ri!pagingInfo.sort
                  )
                ),
                showWhen: ri!pagingInfo.startIndex + ri!pagingInfo.batchSize <= ri!totalCount
              ),
              linkStyle: "STANDALONE"
            ),
            #"SYSTEM_SYSRULES_richTextIcon"(
              icon: "angle-right-bold",
              altText: "Last Page",
              caption: "Last Page",
              link: #"SYSTEM_SYSRULES_dynamicLink"(
                saveInto: a!save(
                  ri!pagingInfo,
                  #"SYSTEM_SYSRULES_pagingInfo"(
                    startIndex: if(
                      mod(ri!totalCount,ri!countPerPage ) = 0,
                      ri!totalCount - (ri!countPerPage-1),
                      ri!totalCount - mod(ri!totalCount, ri!countPerPage) + 1
                    ),
                    batchSize: ri!pagingInfo.batchSize,
                    sort: ri!pagingInfo.sort
                  )
                ),
                showWhen: and(
                  (
                    ri!totalCount - mod(ri!totalCount, ri!countPerPage)
                  ) <= ri!totalCount,
                  ri!pagingInfo.startIndex + ri!pagingInfo.batchSize <= ri!totalCount
                )
              ),
              linkStyle: "STANDALONE"
            )
          },
          color: if(
            (
              ri!pagingInfo.startIndex + ri!pagingInfo.batchSize
            ) > ri!totalCount,
            "SECONDARY",
            "ACCENT"
          )
        )
      },
      align: "CENTER"
    )
  },
  height: "AUTO",
  showWhen: ri!totalCount > ri!countPerPage,
  style: "NONE",
  padding: "STANDARD",
  marginBelow: "STANDARD",
  showBorder: false
)

a!localVariables(
  local!searchText: rule!AS_CO_UT_initializeBlankLocalVariable(
    type: 'type!{http://www.appian.com/ae/types/2009}Text'
  ),
  local!taskCategories: rule!AS_GSS_TMG_QE_getTaskCategories(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  local!taskCategoryFilter: rule!AS_CO_UT_initializeBlankLocalVariable(
    type: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_R_TaskCategory?list'
  ),
  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            rule!AS_CO_DSP_displayHeader(
              headerText: "Tasks"
            )
          }
        )
      }
    ),
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            #"SYSTEM_SYSRULES_ButtonArrayLayout"(
              buttons: {
                #"SYSTEM_SYSRULES_forEach"(
                  items: ri!subActionOptions,
                  expression: rule!AS_CO_BTN_toolbarButton(
                    accessibilityText: if(
                      fv!index = 1,
                      "Press enter to add item",
                      "Press enter to import item"
                    ),
                    label: fv!item,
                    saveInto: {
                      a!save(ri!selectedSubAction, fv!index + 1)
                    },
                    icon: if(fv!index = 1, "PLUS", "file-excel-o")
                  )
                )
              },
              showWhen: ri!allowManageTaskRefs,
              align: "START"
            )
          }
        ),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            rule!AS_CO_CP_multipleDropdownField(
              labelPosition: "COLLAPSED",
              choiceLabels: local!taskCategories.categoryName,
              choiceValues: local!taskCategories,
              placeholderLabel: rule!AS_GSS_TMG_UI_dropdownPlaceholder_filters(
                i18nData: ri!i18nData,
                bundleKey: "plc_Categories"
              ),
              value: local!taskCategoryFilter,
              saveInto: local!taskCategoryFilter
            )
          },
          width: "MEDIUM"
        ),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            rule!AS_CO_CP_searchField(
              accessibilityText: "Press Enter to search process items",
              value: local!searchText,
              saveInto: local!searchText,
              placeholder: rule!AS_GSS_TMG_pickerPlaceholder(
                i18nData: ri!i18nData,
                bundleKey: "lbl_Tasks"
              )
            )
          },
          width: "MEDIUM"
        )
      },
      marginBelow: "NONE"
    ),
    rule!AS_GSS_TMG_GRD_referenceTaskGrid(
      allowLinkSelection: ri!allowManageTaskRefs,
      selectedTaskRefs: ri!selectedTaskRef,
      i18nData: ri!i18nData,
      allowCheckboxSelection: false,
      taskCategoryFilter: local!taskCategoryFilter,
      triggerRefresh: ri!triggerRefresh,
      searchText: local!searchText,
      additionalLinkSelectionSaves: {
        a!save(
          ri!selectedSubAction,
          /*Manage tasks interface*/
          2
        )
      },
      configurationLevelCode: cons!AS_GSS_TMG_ENUM_TASK_CONFIGURATION_LEVEL_CODE_AD_HOC
    )
  }
)

a!localVariables(
  local!i18nData:rule!AS_GSS_CO_UT_loadBundleFromFolder(langISOCode: null),
  local!triggerRefresh: now(),
  local!selectedTaskCategory,
  if(
    rule!AS_CO_UT_isBlank(
      local!selectedTaskCategory
    ),
    rule!AS_GSS_TMG_CPS_viewTaskCategories(
      i18nData: local!i18nData,
      selectedTaskCategory: local!selectedTaskCategory,
      triggerRefresh: local!triggerRefresh
    ),
    rule!AS_GSS_TMG_CPS_manageTaskCategory(
      i18nData: local!i18nData,
      selectedTaskCategory: local!selectedTaskCategory,
      triggerRefresh: local!triggerRefresh
    )
  )
)

a!localVariables(
  local!numTasksToDisplay: 2,
  /*Return the possible task fields which we are mapping to*/
  local!taskFields: rule!AS_GSS_TMG_BL_importTasksFromExcelFieldsList(),
  /*Parse the task import data from the Excel document*/
  local!taskImportData: rule!AS_GSS_TMG_UT_readExcelTasks(
    excelDocument: ri!taskImportDocument,
    numberOfColumns: count(local!taskFields)
  ),
  /*Return an array of headers from Excel with their matching task field*/
  local!excelColumns: #"SYSTEM_SYSRULES_forEach"(
    items: local!taskImportData.headers,
    expression: {
      excelHeader: fv!item,
      taskField: index(
        rule!AS_CO_UT_filterCdtByField(
          cdt: local!taskFields,
          field: "label",
          value: fv!item
        ),
        1,
        null
      )
    }
  ),
  /*The document isn't blank and the parsing returned a valid value*/
  local!fileSuccessfullyUploaded: and(
    rule!AS_CO_UT_isNotBlank(ri!taskImportDocument),
    rule!AS_CO_UT_isNotBlank(local!taskImportData)
  ),
  local!importedRefTasks: rule!AS_GSS_TMG_UT_mapImportTasksToReferenceTasks(
    taskImportData: local!taskImportData,
    excelColumns: local!excelColumns,
    taskTypes: ri!taskTypes,
    taskCategories: ri!taskCategories,
    i18nData: ri!i18nData,
    documentTypes: ri!documentTypes,
    documentTemplates: ri!documentTemplates
  ),
  local!existingTaskRefs: rule!AS_GSS_TMG_QE_getTaskRefs(
    taskName: local!importedRefTasks.taskName,
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    configurationLevelCode: cons!AS_GSS_TMG_ENUM_TASK_CONFIGURATION_LEVEL_CODE_AD_HOC
  ),
  local!refTasksToAdd: a!refreshVariable(
    value: rule!AS_CO_UT_joinListsByField(
      listOne: local!importedRefTasks,
      listTwo: local!existingTaskRefs,
      field: "taskName"
    ),
    refreshOnVarChange: { local!importedRefTasks.taskName }
  ),
  local!showWarningMesage: count(local!existingTaskRefs) > 0,
  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            /*File upload field*/
            rule!AS_CO_CP_fileUploadField(
              accessibility: "Press Enter to upload file",
              label: "Excel File",
              instructions: rule!AS_GSS_TXT_documentUploadInstructions(
                bundle: ri!i18nData,
                showAllowedFileTypes: false(),
                showMaxDocumentLimit: true(),
                maxDocumentsAllowed: 1,
                initialInstructions: if(
                  local!fileSuccessfullyUploaded,
                  null,
                  rule!AS_GAM_CO_I18N_UT_displayLabel(
                    bundle: ri!i18nData,
                    bundleKey: "ins_TaskImportExcelHeaders",
                    arguments: rule!AS_CO_UT_separateListItemsInSentence(
                      list: #"SYSTEM_SYSRULES_forEach"(
                        items: local!taskFields.label,
                        expression: rule!AS_CO_UT_wrapTextInQuotes(text: fv!item)
                      ),
                      finalSeparator: "and"
                    )
                  )
                )
              ),
              target: cons!AS_GSS_TMG_FLD_SETTINGS_IMPORT_FILES,
              maxSelections: 1,
              value: ri!taskImportDocument,
              saveInto: { ri!taskImportDocument }
            ),
            /*Sample Import File*/
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "COLLAPSED",
              value: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: "Sample Import Excel File",
                  link: rule!AS_GAM_CP_documentDownloadLink(
                    document: cons!AS_GSS_TMG_DOC_SAMPLE_TASK_IMPORT
                  ),
                  linkStyle: "STANDALONE"
                )
              },
              showWhen: rule!AS_CO_UT_not(local!fileSuccessfullyUploaded)
            )
          }
        ),
        #"SYSTEM_SYSRULES_columnLayout"()
      }
    ),
    #"SYSTEM_SYSRULES_sectionLayout_v1"(
      contents: {
        #"SYSTEM_SYSRULES_gridLayout"(
          headerCells: {
            #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
              label: "Excel Column"
            ),
            #"SYSTEM_SYSRULES_forEach"(
              items: enumerate(local!numTasksToDisplay),
              expression: #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
                label: rule!AS_GAM_CO_I18N_UT_displayLabel(
                  bundle: ri!i18nData,
                  bundleKey: "lbl_Row",
                  arguments: {
                    fv!index,
                    count(a!flatten(local!taskImportData.data))
                  }
                )
              )
            ),
            #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
              label: rule!AS_GAM_CO_I18N_UT_displayLabel(
                bundle: ri!i18nData,
                bundleKey: "lbl_RowMore",
                arguments: max(
                  0,
                  count(a!flatten(local!taskImportData.data)) - local!numTasksToDisplay
                )
              )
            ),
            #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
              label: "Map Columns to Task Fields"
            )
          },
          columnConfigs: {
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 10),
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 10),
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 10),
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 5),
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 10)
          },
          rows: #"SYSTEM_SYSRULES_forEach"(
            items: local!excelColumns,
            expression: #"SYSTEM_SYSRULES_gridRowLayout"(
              contents: {
                #"SYSTEM_SYSRULES_richTextDisplayField"(
                  label: "Column",
                  value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: fv!item.excelHeader,
                    style: "STRONG"
                  )
                ),
                a!localVariables(
                  local!excelHeader: fv!item.excelHeader,
                  #"SYSTEM_SYSRULES_forEach"(
                    items: enumerate(local!numTasksToDisplay),
                    expression: #"SYSTEM_SYSRULES_richTextDisplayField"(
                      label: "Row",
                      value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: index(
                          local!taskImportData.data[local!excelHeader],
                          fv!index,
                          null
                        ),
                        style: "EMPHASIS"
                      )
                    )
                  )
                ),
                #"SYSTEM_SYSRULES_richTextDisplayField"(
                  value: #"SYSTEM_SYSRULES_richTextIcon"(icon: "arrow-right"),
                  align: "RIGHT"
                ),
                rule!AS_CO_CP_dropdownField(
                  label: "Field",
                  required: true(),
                  choiceLabels: local!taskFields.label,
                  choiceValues: local!taskFields,
                  placeholderLabel: rule!AS_GSS_TMG_UI_dropdownPlaceholder_formInputs(
                    i18nData: ri!i18nData,
                    bundleKey: "lbl_TaskField"
                  ),
                  value: fv!item.taskField,
                  validations: if(
                    rule!AS_CO_UT_checkIfDuplicateExistsInArrayByIndex(
                      array: local!excelColumns.taskField,
                      index: fv!index
                    ),
                    "You cannot map multiple columns to the same task field",
                    null
                  ),
                  saveInto: fv!item.taskField
                )
              }
            )
          ),
          showWhen: local!fileSuccessfullyUploaded
        ),
        rule!AS_CO_DSP_warningBanner(
          showWhen: and(
            local!showWarningMesage,
            local!fileSuccessfullyUploaded
          ),
          label: rule!AS_GAM_CO_I18N_UT_displayLabel(
            bundle: ri!i18nData,
            bundleKey: "ins_SkippedTasksThatAreAlreadyInTheSystem",
            arguments: count(local!existingTaskRefs)
          ),
          secondaryLabel: "",
          acknowledgementSaveInto: { a!save(local!showWarningMesage, false) }
        )
      },
      showWhen: rule!AS_CO_UT_isNotBlank(ri!taskImportDocument),
      validations: #"SYSTEM_SYSRULES_validationMessage"(
        message: "Error parsing the file, please ensure you've uploaded a .xlsx Excel file type and check the format against the Sample Import Excel File",
        validateAfter: "REFRESH",
        showWhen: rule!AS_CO_UT_not(local!fileSuccessfullyUploaded)
      )
    ),
    #"SYSTEM_SYSRULES_ButtonLayout"(
      primaryButtons: #"SYSTEM_SYSRULES_ButtonWidget"(
        accessibilityText: "Press Enter to go to next step",
        label: "Next",
        saveInto: {
          a!save(ri!importStep, ri!importStep + 1),
          a!save(
            ri!allImportedTasks,
            local!refTasksToAdd
          )
        },
        size: "SMALL",
        disabled: rule!AS_CO_UT_not(local!fileSuccessfullyUploaded),
        validate: true()
      ),
      secondaryButtons: #"SYSTEM_SYSRULES_ButtonWidget"(
        accessibilityText: "Press Enter to go to previous step",
        label: "Back",
        saveInto: {
          rule!AS_CO_UT_saveNull(ri!allImportedTasks),
          rule!AS_CO_UT_saveNull(ri!taskImportDocument),
          a!save(ri!selectedSubAction, 1)
        },
        size: "SMALL"
      )
    )
  }
)

