a!localVariables(
  local!taskBehaviorTypes: rule!AS_GSS_TMG_QE_getTaskBehaviorTypes(
    additionalFilters: #"SYSTEM_SYSRULES_queryFilter"(
      field: "taskBehaviorTypeId",
      operator: "in",
      value: {
        cons!AS_GSS_TMG_CONS_BEHAVIOR_TYPE_ID_FOR_CHECKLIST_HOME_TOOLBAR
      }
    )
  ),
  local!taskBehaviorChoiceValues: local!taskBehaviorTypes.taskBehaviorTypeId,
  local!taskBehaviourChoiceLabels: #"SYSTEM_SYSRULES_forEach"(
    items: local!taskBehaviorTypes,
    expression: rule!AS_CO_UT_displayRefDataLabelSingle(
      singleData: fv!item,
      labelField: "behaviorDisplayName",
      i18nData: ri!i18nData
    )
  ),
  local!triggerRefresh: a!refreshVariable(
    value: now(),
    refreshAfter: "RECORD_ACTION"
  ),
  local!checklistData: rule!AS_GSS_TMG_QE_getRuntimeTask(
    triggerRefresh: local!triggerRefresh,
    taskStatusIds: cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
    evaluationId: ri!evaluationId,
    additionalFilters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: "taskBehaviorType.behaviorTypeCode",
        operator: "<>",
        value: cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_TYPE_CODE_AUTOMATED
      )
    },
    additionalLogicalExpressions: rule!AS_GSS_TMG_UT_defaultFilterForTasksInHomePage(evaluationId: ri!evaluationId),
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  local!assignedUsers: reject(
    rule!AS_CO_UT_isBlank(_),
    {
      if(
        rule!AS_CO_UT_isBlank(local!checklistData),
        null,
        union(
          local!checklistData.userAssignee,
          local!checklistData.userAssignee
        )
      )
    }
  ),
  #"SYSTEM_SYSRULES_columnsLayout"(
    columns: {
      #"SYSTEM_SYSRULES_columnLayout"(
        contents: rule!AS_CO_CP_dropdownField(
          placeholderLabel: "Select a task type",
          choiceLabels: local!taskBehaviourChoiceLabels,
          choiceValues: local!taskBehaviorChoiceValues,
          value: ri!selectedChecklistBehaviorType,
          saveInto: { ri!selectedChecklistBehaviorType }
        )
      ),
      #"SYSTEM_SYSRULES_columnLayout"(
        contents: rule!AS_CO_CP_dropdownField(
          placeholderLabel: "Select a user",
          choiceLabels: #"SYSTEM_SYSRULES_forEach"(
            items: local!assignedUsers,
            expression: rule!AS_CO_UT_displayUser(user: fv!item)
          ),
          choiceValues: local!assignedUsers,
          value: ri!selectedUser,
          saveInto: { ri!selectedUser }
        )
      ),
      #"SYSTEM_SYSRULES_columnLayout"(
        contents: {
          #"SYSTEM_SYSRULES_sideBySideLayout"(
            items: {
              #"SYSTEM_SYSRULES_sideBySideItem"(
                item: rule!AS_CO_INP_dateField(
                  value: ri!selectedStartDate,
                  saveInto: ri!selectedStartDate
                ),
                width: "MINIMIZE"
              ),
              #"SYSTEM_SYSRULES_sideBySideItem"(
                item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                  value: {
                    #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: "to"
                    )
                  }
                ),
                width: "MINIMIZE"
              ),
              #"SYSTEM_SYSRULES_sideBySideItem"(
                item: rule!AS_CO_INP_dateField(
                  value: ri!selectedEndDate,
                  saveInto: ri!selectedEndDate,
                  validations: if(
                    ri!selectedEndDate < ri!selectedStartDate,
                    "End date should be greater than start date",
                    ""
                  )
                )
              )
            },
            alignVertical: "MIDDLE"
          )
        }
      )
    },
    showWhen: ri!showChecklistHomeToolbar
  )
)

{
  rule!AS_CO_CPS_cardsAsSelection(
    choiceIcons: ri!refData.refIcon,
    choiceLabels: ri!refData.refLabel,
    choiceValues: ri!refData.refDataId,
    value: ri!task.review.decision.refDataId,
