  local!refData: rule!AS_GSS_QE_getRefDataByTypes(
    refTypes: cons!AS_GSS_REF_TYPE_ITEM_DURATION_UNIT
  ),
  #"SYSTEM_SYSRULES_gridLayout"(
    emptyGridMessage: "No tasks available",
    headerCells: {
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: ""),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Task Name"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Task Type"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Description"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Precedents",
        helpTooltip: "Tasks without precedents will be kicked off upon submission"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Assigned Group" & " * "
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Days from Start" & " * ",
        helpTooltip: "The number of days from when the approach is selected that this task should start. This will be used with the duration to calculate the due date."
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Duration" & " * "
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Duration Unit" & " * "
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "")
    },
    columnConfigs: {
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "ICON"),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
      /*Make description and precedents fields larger*/
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 2),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 2),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "ICON")
    },
    rows: {
      #"SYSTEM_SYSRULES_forEach"(
        items: if(
          rule!AS_CO_UT_isBlank(local!taskIndicesForCategory),
          {},
          ri!selectedTemplate.templateTasks[local!taskIndicesForCategory]
        ),
        expression: {
          #"SYSTEM_SYSRULES_gridRowLayout"(
            contents: {
              /*index*/
              rule!AS_CO_CP_integerField(
                readOnly: true,
                value: fv!index,
                align: "CENTER"
              ),
              /*name*/
              rule!AS_CO_INP_textField(
                required: true,
                readOnly: true,
                value: fv!item.taskRef.taskName
              ),
              /*type*/
              rule!AS_CO_INP_textField(
                required: true,
                readOnly: true,
                value: rule!AS_CO_UT_displayRefDataLabelSingle(
                  singleData: fv!item.taskRef.taskBehaviorType,
                  labelField: "behaviorDisplayName",
                  i18nData: ri!i18nData
                )
              ),
              /*description*/
              rule!AS_CO_INP_textField(
                value: fv!item.taskDesc,
                validations: rule!AS_CO_VD_textValidation(
                  value: fv!item.taskDesc,
                  maxLength: cons!AS_CO_ENUM_TEXT_LENGTH_LONG
                ),
                saveInto: fv!item.taskDesc
              ),
              /*precedents*/
              a!localVariables(
                local!precTaskOptions: a!refreshVariable(
                  value: rule!AS_CO_UT_rejectCdtByField(
                    cdt: ri!selectedTemplate.templateTasks.taskRef,
                    field: "taskRefId",
                    value: fv!item.taskRef.taskRefId
                  ),
                  refreshOnVarChange: ri!selectedTemplate
                ),
                {
                  rule!AS_CO_CP_multipleDropdownField(
                    placeholderLabel: "Select Precedents",
                    choiceLabels: local!precTaskOptions.taskName,
                    choiceValues: local!precTaskOptions.taskRefId,
                    value: a!flatten(fv!item.templateTaskPrecedents).taskRefId_precedent,
                    saveInto: {
                      a!save(
                        fv!item.templateTaskPrecedents,
                        #"SYSTEM_SYSRULES_forEach"(
                          items: save!value,
                          expression: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_R_TemplateTask_Precedent'(taskRefId_precedent: fv!item)
                        )
                      )
                    },
                    disabled: rule!AS_CO_UT_isBlank(local!precTaskOptions)
                  )
                }
              ),
              /*default assignee*/
              rule!AS_CO_CP_pickerFieldGroups(
                required: true,
                maxSelections: 1,
                groupFilter:cons!AS_GSS_TMG_GRP_TASK_RECIPIENTS,
                value: fv!item.groupAssignee,
                saveInto: fv!item.groupAssignee
              ),
              /*Days From Start*/
              rule!AS_CO_CP_integerField(
                required: true,
                value: fv!item.noOfDaysFromStart,
                saveInto: fv!item.noOfDaysFromStart,
                validations: rule!AS_CO_VD_numberValidation(
                  value: fv!item.noOfDaysFromStart,
                  minSize: 1,
                  minSizeMessage: "Please enter a value greater than 0",
                  maxSize: cons!AS_GSS_TMG_TEXT_LENGTH_FOR_DAYS_FROM_START,
                  maxSizeMessage: "Please enter a value less than or equal to" & " " & cons!AS_GSS_TMG_TEXT_LENGTH_FOR_DAYS_FROM_START
                )
              ),
              /*Duration*/
              rule!AS_CO_CP_integerField(
                required: true,
                value: fv!item.duration,
                saveInto: fv!item.duration,
                placeholder: "Enter duration",
                validations: rule!AS_CO_VD_numberValidation(
                  value: fv!item.duration,
                  minSize: 1,
                  minSizeMessage: "Please enter a duration value greater than 0",
                  maxSize: 1000,
                  maxSizeMessage: "Please enter a duration value less than or equal to 1000"
                )
              ),
              /*Duration Unit*/
              rule!AS_CO_CP_dropdownField(
                required: true,
                choiceLabels: local!refData.refLabel,
                choiceValues: local!refData.refDataId,
                value: fv!item.durationUnit.refDataId,
                saveInto: fv!item.durationUnit.refDataId,
                placeholderLabel: "Select a unit"
              ),
              /*remove task*/
              rule!AS_CO_DSP_removeIcon(
                caption: "Remove Task",
                /*remove task as precedent for other tasks, then remove task from template*/
                saveInto: rule!AS_GSS_TMG_UT_removeTaskFromTemplateSaves(
                  selectedTemplate: ri!selectedTemplate,
                  taskRefIdToRemove: fv!item.taskRef.taskRefId
                ),
                removalType: cons!AS_CO_ENUM_REMOVAL_ICON_TYPE_CLEAR
              )
            }
          )
        }
      )
    }
  )
)

