a!localVariables(
  /* Used to track if we are creating a new task or editing an existing one
  If there is an ID populated, this must be a preexisting task that is being edited/viewed */
  local!isExistingTaskRef: rule!AS_CO_UT_isNotBlank(ri!selectedTaskRef.taskRefId),
  {
    rule!AS_CO_DSP_displayHeader(
      headerText: rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundle: ri!i18nData,
        bundleKey: if(
          local!isExistingTaskRef,
          "lbl_EditTask",
          "lbl_AddTask"
        )
      )
    ),
    rule!AS_GSS_TMG_CP_warnCard(
      showWhen: local!isExistingTaskRef,
      label: "Edits to this task will be reflected on all approaches that reference it.",
      secondaryLabel: "In-flight tasks will not be affected."
    ),
    rule!AS_GSS_TMG_CPS_referenceTaskContents(
      i18nData: ri!i18nData,
      taskRef: ri!selectedTaskRef
    ),
    rule!AS_CO_CPS_cancelSaveButtons(
      confirmAccessibilityText: if(
        local!isExistingTaskRef,
        "Press Enter to save changes",
        "Press Enter to add item"
      ),
      cancelAccessibilityText: "Press Enter to cancel changes",
      confirmLabel: rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundle: ri!i18nData,
        bundleKey: if(
          local!isExistingTaskRef,
          "btn_SaveChanges",
          "btn_AddTask"
        )
      ),
      cancelLabel: "Cancel",
      confirmSaveInto: {
        rule!AS_ADT_UT_startAuditProcess(
          newObjects: ri!selectedTaskRef,
          objectType: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_R_TaskRef',
          objectDse: cons!AS_GSS_TMG_ENT_R_TASK_REF,
          auditDse: cons!AS_GSS_TMG_ENT_A_R_TASK_REF,
          auditConfig: rule!AS_GSS_TMG_ADT_BL_auditConfig_R_TaskRef(),
          onSuccess: {
            rule!AS_CO_UT_triggerRefresh(triggerRefresh: ri!triggerRefresh),
            rule!AS_CO_UT_saveNull(valueToNull: ri!selectedTaskRef),
            a!save(ri!selectedSubAction, 1)
          }
        )
      },
      cancelSaveInto: {
        rule!AS_CO_UT_saveNull(valueToNull: ri!selectedTaskRef),
        a!save(ri!selectedSubAction, 1)
      },
      includeDivider: true
    )
  }
)

