      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "ICON")
    },
    rows: {
      #"SYSTEM_SYSRULES_forEach"(
        items: ri!selectedReview.templateTasks,
        expression: {
          #"SYSTEM_SYSRULES_gridRowLayout"(
            contents: {
              /*index*/
              rule!AS_CO_CP_integerField(
                readOnly: true,
                value: fv!index,
                align: "CENTER"
              ),
              a!localVariables(
                local!precTaskOptions: a!refreshVariable(
                  value: rule!AS_CO_UT_rejectCdtByField(
                    cdt: ri!selectedReview.templateTasks,
                    field: "groupAssignee",
                    value: fv!item.groupAssignee
                  ),
                  refreshOnReferencedVarChange: true
                ),
                local!choiceLabels: a!refreshVariable(
                  value: #"SYSTEM_SYSRULES_forEach"(
                    items: rule!AS_CO_UT_rejectNullValuesFromArray(
                      union(
                        local!precTaskOptions.groupAssignee,
                        local!precTaskOptions.groupAssignee
                      )
                    ),
                    expression: group(fv!item, "groupName"),

                  ),
                  refreshOnReferencedVarChange: true
                ),
                local!choiceValues: a!refreshVariable(
                  value: rule!AS_CO_UT_rejectNullValuesFromArray(
                    union(
                      local!precTaskOptions.groupAssignee,
                      local!precTaskOptions.groupAssignee
                    )
                  ),
                  refreshOnReferencedVarChange: true
                ),
                /*precedents*/
                {
                  rule!AS_CO_CP_multipleDropdownField(
                    placeholderLabel: "Select Precedents",
                    choiceLabels: local!choiceLabels,
                    choiceValues: local!choiceValues,
                    value: a!flatten(fv!item.templateTaskPrecedents).groupId_precedent,
                    disabled: rule!AS_CO_UT_isBlank(local!precTaskOptions.groupAssignee),
                    readOnly: rule!AS_CO_UT_isBlank(local!precTaskOptions.groupAssignee),
                    saveInto: {
                      a!save(
                        fv!item.templateTaskPrecedents,
                        #"SYSTEM_SYSRULES_forEach"(
                          items: save!value,
                          expression: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_R_TemplateTask_Precedent'(groupId_precedent: fv!item)
                        )
                      )
                    }
                  ),
                  /*default assignee*/
                  rule!AS_CO_CP_pickerFieldGroups(
                    required: true,
                    maxSelections: 1,
                    groupFilter: cons!AS_GSS_TMG_GRP_TASK_RECIPIENTS,
                    value: fv!item.groupAssignee,
                    validations: if(
                      contains(
                        remove(
                          ri!selectedReview.templateTasks.groupAssignee,
                          fv!index
                        ),
                        fv!item.groupAssignee
                      ),
                      "Group exists already",
                      ""
                    ),
                    saveInto: {
                      fv!item.groupAssignee,
                      /*to update the task precedents*/
                      rule!AS_GSS_TMG_UT_updatePrecedentsOnUpdatingGroupAssignee(selectedReview: ri!selectedReview)
                    },
                    placeholder: "Select a group"
                  )
                }
              ),
              /*Duration*/
              rule!AS_CO_CP_integerField(
                required: true,
                value: fv!item.duration,
                validations: rule!AS_CO_VD_numberValidation(
                  value: fv!item.duration,
                  minSize: 1,
                  minSizeMessage: "Please enter a duration value greater than 0",
                  maxSize: 1000,
                  maxSizeMessage: "Please enter a duration value less than or equal to 1000"
                ),
                saveInto: fv!item.duration,
                placeholder: "Enter duration"
              ),
              /*Duration Unit*/
              rule!AS_CO_CP_dropdownField(
                required: true,
                choiceLabels: local!refData.refLabel,
                choiceValues: local!refData.refDataId,
                placeholderLabel: "Select a unit",
                value: fv!item.durationUnit.refDataId,
                saveInto: fv!item.durationUnit.refDataId
              ),
              /*remove task*/
              rule!AS_CO_DSP_removeIcon(
                caption: "Remove Task",
                /*remove task as precedent for other tasks, then remove task from template*/
                saveInto: {
                  rule!AS_GSS_TMG_UT_removeItemFromReviewSaves(
                    selectedReview: ri!selectedReview,
                    groupIdToRemove: fv!item.groupAssignee,
                    templateTaskIndexToRemove: fv!index
                  )
                },
                removalType: cons!AS_CO_ENUM_REMOVAL_ICON_TYPE_CLEAR
              )
            }
          )
        }
      )
    },
    addRowLink: #"SYSTEM_SYSRULES_dynamicLink"(
      label: "Review Group",
      value: {
        rule!AS_CO_UT_cast(
          type: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_R_TemplateTask',
          value: {
            templateTaskId: null,
            /*Populating the task ref details for "Review Document*/
            taskRef: local!refTask,
            templateTaskPrecedents: {}
          }
        )
      },
      saveInto: {
        a!save(
          ri!selectedReview.templateTasks,
          append(
            ri!selectedReview.templateTasks,
            save!value
          )
        )
      }
    )
  )
)

