a!localVariables(
  local!searchTerm,
  {
    #"SYSTEM_SYSRULES_headingField"(
      text: "Search Vendors",
      size: "SMALL",
      headingTag: "H2",
      fontWeight: "BOLD"
    ),
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: rule!AS_CO_INP_textField(
            label: "Search Vendors",
            instructions: "Search by UEI or CAGE",
            labelPosition: "COLLAPSED",
            value: local!searchTerm,
            validations: rule!AS_GSS_VD_enteredVendorDataValidationMessage(
              searchTerm: local!searchTerm,
              cage: upper(
                if(
                  len(local!searchTerm) = cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_CAGE,
                  local!searchTerm,
                  null
                )
              ),
              uniqueEntityId: upper(
                if(
                  len(local!searchTerm) = cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_UEI,
                  local!searchTerm,
                  null
                )
              ),
              bundle:ri!bundle
            ),
            saveInto: {
              local!searchTerm,
              a!save(ri!cage, null),
              a!save(ri!uniqueEntityId, null)
            }
          ),
          width: "MEDIUM"
        ),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: #"SYSTEM_SYSRULES_ButtonArrayLayout"(
            buttons: #"SYSTEM_SYSRULES_buttonWidget_v1"(
              label: if(
                cons!AS_GSS_TOGGLE_AUTOMATION_TESTING_ENABLED,
                "Search",
                ""
              ),
              accessibilityText: "Search",
              icon: "search",
              value: true(),
              saveInto: {
                ri!searchAction,
                a!save(ri!searchTerm, local!searchTerm),
                a!save(ri!returnedVendors, null),
                a!match(
                  value: local!searchTerm,
                  whenTrue: len(local!searchTerm) = cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_CAGE,
                  then: {
                    a!save(ri!cage, local!searchTerm),
                    a!save(ri!uniqueEntityId, null)
                  },
                  whenTrue: len(local!searchTerm) = cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_UEI,
                  then: {
                    a!save(ri!uniqueEntityId, local!searchTerm),
                    a!save(ri!cage, null)
                  },
                  default: {
                    a!save(ri!cage, null),
                    a!save(ri!uniqueEntityId, null)
                  }
                ),
                a!localVariables(
                  /*Calls the Integrations*/
                  local!apiDataVendors: rule!AS_GSS_mapDataFromSAMGovToRefVendorRecord(
                    cage: upper(
                      if(
                        len(local!searchTerm) = cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_CAGE,
                        local!searchTerm,
                        null
                      )
                    ),
                    uniqueEntityId: upper(
                      if(
                        len(local!searchTerm) = cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_UEI,
                        local!searchTerm,
                        null
                      )
                    )
                  ),
                  /*Check against vendor records*/
                  local!refDataVendors: rule!AS_GSS_QR_getReferenceVendors(
                    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
                    logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
                      operator: "OR",
                      filters: {
                        #"SYSTEM_SYSRULES_queryFilter"(
                          field: recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.formattedCageCode,
                          operator: "=",
                          value: upper(local!searchTerm)
                        ),
                        #"SYSTEM_SYSRULES_queryFilter"(
                          field: recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.formattedUniqueEntityId,
                          operator: "=",
                          value: upper(local!searchTerm)
                        )
                      }
                    )
                  ),
                  {
                    a!save(
                      ri!returnedVendors,
                      rule!AS_GSS_UT_finalizedApiAndRefVendorsRecordList(
                        refVendors: local!refDataVendors,
                        apiVendors: local!apiDataVendors.data
                      )
                    ),
                    a!save(
                      ri!errorMessage,
                      local!apiDataVendors.error
                    )
                  }
                )
              },
              size: "SMALL",
              color: "SECONDARY",
              disabled: or(
                rule!AS_CO_UT_isBlank(value: local!searchTerm),
                rule!AS_CO_UT_isNotBlank(
                  input: rule!AS_GSS_VD_enteredVendorDataValidationMessage(
                    searchTerm: local!searchTerm,
                    cage: upper(
                      if(
                        len(local!searchTerm) = cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_CAGE,
                        local!searchTerm,
                        null
                      )
                    ),
                    uniqueEntityId: upper(
                      if(
                        len(local!searchTerm) = cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_UEI,
                        local!searchTerm,
                        null
                      )
                    ),
                    bundle: ri!bundle
                  )
                )
              )
            ),
            align: "START",
            marginBelow: "NONE"
          )
        )
      },
      alignVertical: "TOP",
      marginBelow: "NONE",
      spacing: "DENSE"
    )
  }
)

a!localVariables(
  local!isExistingTemplate: rule!AS_CO_UT_isNotBlank(ri!selectedReview.templateId),
  /*Boolean to show warning message when the status is made inactive*/
  local!showWarning: false,
  {
    /*form header*/
    rule!AS_CO_DSP_displayHeader(
      headerText: if(
        local!isExistingTemplate,
        "Edit Review",
        "Add Review"
      )
    ),
    /*template name, description, mapping fields*/
    rule!AS_GSS_TMG_CPS_manageReviewProperties(
      selectedReview: ri!selectedReview,
      i18nData: ri!i18nData,
      /*readOnly: false,*/
      showWarning: local!showWarning,
      triggerRefresh: ri!triggerRefresh
    ),
    /*Review grid*/
    rule!AS_GSS_TMG_CPS_GRD_manageReviewGrid(
      i18nData: ri!i18nData,
      selectedReview: ri!selectedReview,
      /*triggerRefresh: ri!triggerRefresh*/

    ),
    /*save and cancel buttons*/
    rule!AS_CO_CPS_cancelSaveButtons(
      confirmLabel: rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundle: ri!i18nData,
        bundleKey: if(
          local!isExistingTemplate,
          "btn_SaveChanges",
          "btn_AddReview"
        )
      ),
      cancelLabel: "Cancel",
      confirmSaveInto: {
        a!save(local!showWarning, false),
        a!save(
          ri!selectedReview.templateType.refDataId,
          cons!AS_GSS_REF_ID_TEMPLATE_TYPE_DOCUMENT_REVIEW
        ),
        rule!AS_ADT_UT_startAuditProcess(
          newObjects: ri!selectedReview,
          objectType: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_R_Template',
          objectDse: cons!AS_GSS_TMG_ENT_R_TEMPLATE,
          auditDse: cons!AS_GSS_TMG_ENT_A_R_TEMPLATE,
          auditConfig: rule!AS_GSS_TMG_ADT_BL_auditConfig_R_Template(),
          onSuccess: {
            rule!AS_CO_UT_triggerRefresh(triggerRefresh: ri!triggerRefresh),
            rule!AS_CO_UT_saveNull(valueToNull: ri!selectedReview)
          }
        )
      },
      cancelSaveInto: {
        a!save(local!showWarning, false),
        rule!AS_CO_UT_saveNull(valueToNull: ri!selectedReview)
      },
      includeDivider: true,
      confirmAccessibilityText: "Press Enter to save changes",
      cancelAccessibilityText: "Press Enter to cancel changes"
    )
  }
)

a!localVariables(
  local!searchText: rule!AS_CO_UT_initializeBlankLocalVariable(
    type: 'type!{http://www.appian.com/ae/types/2009}Text'
  ),
  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            rule!AS_CO_DSP_displayHeader(
              headerText: rule!AS_GAM_CO_I18N_UT_displayLabel(
                bundle: ri!i18nData,
