            #"SYSTEM_SYSRULES_ButtonArrayLayout"(
              buttons: {
                rule!AS_CO_BTN_toolbarButton(
                  label: "Add Custom Task",
                  saveInto: {
                    a!save(
                      ri!newCustomTask.taskCategory,
                      ri!addingTaskCategory
                    )
                  },
                  icon: "PLUS"
                )
              },
              showWhen: ri!showAddCustomTaskBtn,
              align: "START"
            )
          }
        ),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: rule!AS_CO_CP_searchField(
            value: local!searchText,
            saveInto: local!searchText,
            placeholder: rule!AS_GSS_TMG_pickerPlaceholder(
              i18nData: ri!i18nData,
              bundleKey: "lbl_Tasks"
            )
          ),
          width: "MEDIUM"
        )
      },
      alignVertical: if(ri!showAddCustomTaskBtn, "BOTTOM", null),
      marginBelow: "STANDARD"
    ),
    rule!AS_GSS_TMG_GRD_referenceTaskGrid(
      searchText: local!searchText,
      allowLinkSelection: false,
      selectedTaskRefs: ri!selectedTaskRefs,
      i18nData: ri!i18nData,
      allowCheckboxSelection: true,
      taskCategoryFilter: ri!addingTaskCategory,
      taskRefIdsToHide: ri!taskRefIdsToHide,
      triggerRefresh: ri!triggerRefresh,
      configurationLevelCode: cons!AS_GSS_TMG_ENUM_TASK_CONFIGURATION_LEVEL_CODE_TEMPLATE
    ),
    rule!AS_CO_CPS_cancelSaveButtons(
      cancelAccessibilityText: "Press Enter to cancel changes",
      confirmAccessibilityText: "Press Enter to add items to process",
      confirmLabel: "Add Tasks",
      cancelLabel: "Cancel",
      confirmSaveInto: {
        ri!addTaskSaves,
        rule!AS_CO_UT_saveNull(valueToNull: ri!addingTaskCategory),
        rule!AS_CO_UT_saveNull(valueToNull: ri!selectedTaskRefs)
      },
      cancelSaveInto: {
        rule!AS_CO_UT_saveNull(valueToNull: ri!addingTaskCategory),
        rule!AS_CO_UT_saveNull(valueToNull: ri!selectedTaskRefs)
      },
      includeDivider: true
    )
  }
)

a!localVariables(
  /*sheetNumber and hasHeaders can be adapted to be rule inputs if that flexibility is desired*/
  local!sheetNumber: 0,
  local!hasHeaders: true,
  local!excelData: readexcelimportfile(
    ri!excelDocument,
    rule!AS_CO_UT_replaceNull(
      nullableValue: local!sheetNumber,
      replacementValue: 0
    ),
    0,
    ri!numberOfColumns
  ),
  local!importedData:if(
    rule!AS_CO_UT_not(
      local!excelData.success
    ),
    rule!AS_CO_UT_returnNullDictionary(),
    a!localVariables(
      local!headers: #"SYSTEM_SYSRULES_forEach"(
        items: index(
          local!excelData.result,
          1,
          "values",
          {}
        ),
        expression: if(
          local!hasHeaders,
          fv!item,
          concat(
            "col",
            fv!index
          )
        )
      ),
      {
        sheetNumber: local!sheetNumber,
        headers: touniformstring(
          local!headers
        ),
        data: if(
          rule!AS_CO_UT_isBlank(
            local!excelData.result
          ),
          rule!AS_CO_UT_returnNullDictionaryList(),
          a!flatten(#"SYSTEM_SYSRULES_forEach"(
            items: local!excelData.result.values,
            expression: if(
              and(
                local!hasHeaders,
                fv!isFirst
              ),
              {},
              rule!AS_CO_UT_updateCdtFields(
                cdt: rule!AS_CO_UT_returnNullDictionary(),
                fields: local!headers,
                newValues: fv!item
              )
            )
          )
          )
        )
      }
    )
  ),
  if(rule!AS_CO_UT_isBlank(local!importedData.data),null,local!importedData)
)

