a!localVariables(
  local!totalRequiredTasks: rule!AS_CO_UT_queryEntity(
    entity: cons!AS_GSS_TMG_ENT_TASK,
    entityType: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_Task',
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_TOTAL_COUNT,
    triggerRefresh: ri!triggerRefresh,
    filters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: "evaluation.evaluationId",
        operator: "in",
        value: ri!evaluationId
      ),
      #"SYSTEM_SYSRULES_queryFilter"(
        field: "taskStatus.taskStatusId",
        operator: "in",
        value: {
          cons!AS_GSS_TMG_REF_ID_TASK_STATUS_QUEUED,
          cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
          cons!AS_GSS_TMG_REF_ID_TASK_STATUS_IN_PROGRESS,
          cons!AS_GSS_TMG_REF_ID_TASK_STATUS_COMPLETE
        }
      ),
      ri!additionalFilter
    },
    logicalExpression: ri!logicalExpression
  ),
  local!totalCompletedTasks: rule!AS_CO_UT_queryEntity(
    entity: cons!AS_GSS_TMG_ENT_TASK,
    entityType: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_Task',
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_TOTAL_COUNT,
    triggerRefresh: ri!triggerRefresh,
    filters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: "evaluation.evaluationId",
        operator: "in",
