rule!AS_CO_UT_cast(
  type: 'type!{http://www.appian.com/ae/types/2009}Dictionary?list',
  value: {
    /*When the field changes are null, an empty audit dictionary is constructed [Used for reactivated and deactivated templates*/
    if(
      contains(
        {
          cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_DEACTIVATE,
          cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_REACTIVATE
        },
        ri!auditTemplate.auditActionCode
      ),
      {
        templateAuditId: ri!auditTemplate.templateAuditId,
        itemId: ri!auditTemplate.templateId,
        actionCode: ri!auditTemplate.auditActionCode,
        username: ri!auditTemplate.username,
        timestamp: ri!auditTemplate.timestamp,
        fieldChanges: null
      },
      {}
    ),
    /*Template simple field changes*/
    if(
      rule!AS_CO_UT_isBlank(ri!auditTemplate.simpleFieldChanges),
      {},
      #"SYSTEM_SYSRULES_forEach"(
        items: rule!AS_CO_UT_cast(
          type: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_A_R_Template_Field?list',
          value: ri!auditTemplate.simpleFieldChanges
        ),
        expression: {
          templateAuditId: fv!item.templateAuditId,
          itemId: ri!auditTemplate.templateId,
          actionCode:
          /* To display the simple field changes made while Deactivating / Reactivating, the audit code is marked as "MODIFY"*/
          if(
            contains(
              {
                cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_DEACTIVATE,
                cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_REACTIVATE
              },
              ri!auditTemplate.auditActionCode
            ),
            cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_MODIFY,
            ri!auditTemplate.auditActionCode
          ),
          username: ri!auditTemplate.username,
          timestamp: ri!auditTemplate.timestamp,
          fieldChanges: {
            fieldName: fv!item.fieldName,
            oldValue: fv!item.oldValue,
            newValue: fv!item.newValue
          }
        }
      )
    ),
    /*Template Task Changes*/
    #"SYSTEM_SYSRULES_forEach"(
      items: rule!AS_CO_UT_cast(
        type: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_A_R_TemplateTask?list',
        value: ri!auditTemplate.templateTasksChanges
      ),
      expression: a!localVariables(
        local!templateTaskChange: fv!item,
        /*Template Task Simple Field Changes*/
        {
          #"SYSTEM_SYSRULES_forEach"(
            items: rule!AS_CO_UT_cast(
              type: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_A_R_TemplateTask_Field?list',
              value: local!templateTaskChange.simpleFieldChanges
            ),
            expression: {
