              itemId: local!templateTaskChange.templateTaskId,
              actionCode: local!templateTaskChange.auditActionCode,
              username: local!templateTaskChange.username,
              timestamp: local!templateTaskChange.timestamp,
              fieldChanges: {
                fieldName: fv!item.fieldName,
                oldValue: fv!item.oldValue,
                newValue: fv!item.newValue
              }
            }
          ),
          /*Template Task Precedent Changes*/
          #"SYSTEM_SYSRULES_forEach"(
            items: rule!AS_CO_UT_cast(
              type: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_A_R_TemplateTask_Precedent?list',
              value: local!templateTaskChange.templateTaskPrecedentsChanges
            ),
            expression: a!localVariables(
              local!templateTaskPrecedentsChanges: fv!item,
              #"SYSTEM_SYSRULES_forEach"(
                items: rule!AS_CO_UT_cast(
                  type: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_A_R_TemplateTask_Precedent_Field?list',
                  value: local!templateTaskPrecedentsChanges.simpleFieldChanges
                ),
                expression: {
                  templateAuditId: ri!auditTemplate.templateAuditId,
                  itemId: local!templateTaskChange.templateTaskId,
                  actionCode: local!templateTaskPrecedentsChanges.auditActionCode,
                  username: local!templateTaskPrecedentsChanges.username,
                  timestamp: local!templateTaskPrecedentsChanges.timestamp,
                  fieldChanges: {
                    fieldName: fv!item.fieldName,
                    oldValue: fv!item.oldValue,
                    newValue: fv!item.newValue
                  }
                }
              )
            )
          )
        }
      )
    )
  }
)

