data.sail_code:
a!localVariables(
  local!evaluationCriteria: filter(
    rule!AS_CO_UT_isNotBlank,
    {
      ri!evaluationFactors,
      ri!evaluationSubFactors
    }
  ),
  local!missingItems: {
    /* is Evaluation Summary incomplete? */
    if(
      or(
        rule!AS_CO_UT_isBlank(ri!evaluation.evaluationTitle),
        rule!AS_CO_UT_isBlank(ri!evaluation.evaluationNumber),
        rule!AS_CO_UT_isBlank(ri!evaluation.evaluationDesc),
        rule!AS_CO_UT_isBlank(ri!evaluation.evaluationStartDate),
        rule!AS_CO_UT_isBlank(ri!evaluation.evaluationDueDate),
        rule!AS_CO_UT_isBlank(ri!evaluation.evaluationMethod),
        and(
          cons!AS_GSS_TOGGLE_EVALUATION_CHIEF_VISIBILITY,
          rule!AS_CO_UT_isBlank(ri!evaluation.evaluationChief)
        )
      ),
      {
        activeStep: 1,
        message: "Complete the evaluation details",

      },
      {}
    ),
    /* is at least one phase completed and filled out */
    if(
      or(
        /* add extra phase related logic here */
        rule!AS_CO_UT_isBlank(
          rule!AS_CO_UT_filterCdtByField(
            cdt: ri!evaluationPhases,
            field: "isActive",
            value: true
          )
        ),

      ),
      {
        activeStep: 2,
        message: "Add at least one evaluation phase",

      },
      {}
    ),
    /* is at least one criterion created and filled out */
    if(
      /* add extra criteria related logic here */
      or(
        rule!AS_CO_UT_isBlank(
          rule!AS_CO_UT_filterCdtByField(
            cdt: local!evaluationCriteria,
            field: "isActive",
            value: true
          )
        ),
        if(
          rule!AS_CO_UT_isNotBlank(
            rule!AS_CO_UT_filterCdtByField(
              cdt: local!evaluationCriteria,
              field: "isActive",
              value: true
            )
          ),
          or(
            #"SYSTEM_SYSRULES_forEach"(
              items: rule!AS_CO_UT_filterCdtByField(
                cdt: local!evaluationCriteria,
                field: "isActive",
                value: true
              ),
              expression: or(
                rule!AS_CO_UT_isBlank(fv!item.factorNumber),
                rule!AS_CO_UT_isBlank(fv!item.criteriaName),
                rule!AS_CO_UT_isBlank(fv!item.criteriaDesc),
                rule!AS_CO_UT_isBlank(fv!item.refRatingType),
                rule!AS_CO_UT_isBlank(fv!item.criteriaChair),
                rule!AS_CO_UT_isBlank(fv!item.dueDate),

              )
            ),

          ),
          {}
        )
      ),
      {
        activeStep: 3,
        message: "Setup at least one evaluation factor and provide information for added factors/subfactors"
      },
      {}
    ),
    /* Is any of the factor/subfactor weight is blank */
    /* Is factor weight does not equal 100 */
    /* Is any subfactors do not equal to the factor weights*/
    #"SYSTEM_SYSRULES_forEach"(
      items: if(
        ri!evaluation.isWeightedFactorsRequired,
        rule!AS_GSS_VD_validateFactorWeights(
          evaluationFactors: ri!evaluationFactors,
          evaluationSubFactors: ri!evaluationSubfactors,
          bundle: ri!bundle,
          checkForBlankWeights: true()
        ),
        {}
      ),
      expression: { activeStep: 4, message: fv!item }
    ),
    /* is at least one vendor created with an associated document */
    if(
      or(
        rule!AS_CO_UT_isBlank(
          rule!AS_CO_UT_filterCdtByField(
            cdt: ri!evaluationVendors,
            field: "isActive",
            value: true
          )
        ),
        rule!AS_CO_UT_isBlank(
          rule!AS_CO_UT_filterCdtByField(
            cdt: ri!vendorDocs,
            field: "isDeleted",
            value: false
          )
        ),
        if(
          and(
            rule!AS_CO_UT_isNotBlank(
              rule!AS_CO_UT_filterCdtByField(
                cdt: ri!evaluationVendors,
                field: "isActive",
                value: true
              )
            ),
            rule!AS_CO_UT_isNotBlank(
              rule!AS_CO_UT_filterCdtByField(
                cdt: ri!vendorDocs,
                field: "isDeleted",
                value: false
              )
            )
          ),
          or(
            #"SYSTEM_SYSRULES_forEach"(
              items: rule!AS_CO_UT_filterCdtByField(
                cdt: ri!evaluationVendors,
                field: "isActive",
                value: true
              ),
              expression: rule!AS_CO_UT_isBlank(
                wherecontains(
                  fv!item.vendorId,
                  rule!AS_CO_UT_filterCdtByField(
                    cdt: ri!vendorDocs,
                    field: "isDeleted",
                    value: false
                  ).vendorId
                )
              ),

            )
          ),
          {}
        ),

      ),
      {
        activeStep: if(
          ri!evaluation.isWeightedFactorsRequired,
          5,
          4
        ),
        message: "Select at least one vendor and provide the requisite documents",

      },
      {}
    ),
    /* is at least one evaluator present in the evaluation team*/
    if(
      or(
        rule!AS_CO_UT_isBlank(
          rule!AS_CO_UT_filterCdtByField(
            cdt: ri!evaluatorTeams,
            field: "isActive",
            value: true
          )
        ),
        #"SYSTEM_SYSRULES_forEach"(
          items: rule!AS_CO_UT_filterCdtByField(
            cdt: ri!evaluatorTeams,
            field: "isActive",
            value: true
          ),
          expression: rule!AS_CO_UT_isBlank(
            rule!AS_CO_UT_filterCdtByField(
              cdt: fv!item.teamMembership,
              field: "isActive",
              value: true
            )
          ),

        )
      ),
      {
        activeStep: if(
          ri!evaluation.isWeightedFactorsRequired,
          6,
          5
        ),
        message: "Add at least one evaluator to the evaluation team",

      },
      {}
    ),
    /* all created criteria have an assigned due date and evaluator WHEN USE EVAL TEAMS IS SELECTED */
    if(
      or(
        rule!AS_CO_UT_isBlank(
          rule!AS_CO_UT_filterCdtByField(
            cdt: local!evaluationCriteria,
            field: "isActive",
            value: true
          )
        ),
        #"SYSTEM_SYSRULES_forEach"(
          items: rule!AS_CO_UT_filterCdtByField(
            cdt: local!evaluationCriteria,
            field: "isActive",
            value: true
          ),
          expression: rule!AS_CO_UT_isBlank(
            rule!AS_CO_UT_filterCdtByField(
              cdt: fv!item.assignments,
              field: "isActive",
              value: true
            )
          ),

        ),
        #"SYSTEM_SYSRULES_forEach"(
          items: rule!AS_CO_UT_filterCdtByField(
            cdt: local!evaluationCriteria,
            field: "isActive",
            value: true
          ),
          expression: a!localVariables(
            local!evaluationCriterion: fv!item,
            or(
              #"SYSTEM_SYSRULES_forEach"(
                items: rule!AS_CO_UT_filterCdtByField(
                  cdt: local!evaluationCriterion.assignments,
                  field: "isActive",
                  value: true
                ),
                expression: rule!AS_CO_UT_isBlank(fv!item.assignee)
              )
            )
          )
        ),

      ),
      {
        activeStep: if(
          ri!evaluation.isWeightedFactorsRequired,
          7,
          6
        ),
        message: "Assign a due date and evaluator to each factor and sub-factor",

      },
      {}
    ),
    /* one of the three owner fields is populated */
    if(
      and(
        rule!AS_CO_UT_isBlank(ri!evaluation.contractingOfficer),
        rule!AS_CO_UT_isBlank(ri!evaluation.contractingSpecialist),

      ),
      {
        activeStep: 1,
        message: "Add either a contracting officer or contract specialist",

      },
      {}
    )
  },
  {
    disableButton: not(
      rule!AS_CO_UT_isBlank(local!missingItems)
    ),
    missingItems: { local!missingItems }
  }
)

