rule!AS_CO_UT_queryEntity(
  entity: cons!AS_GSS_TMG_ENT_A_R_TEMPLATE,
  entityType: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_A_R_Template',
  returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
  triggerRefresh: ri!triggerRefresh,
  filters: {
    #"SYSTEM_SYSRULES_queryFilter"(
      field: "templateId",
      operator: "=",
      value: ri!templateId
    )
  },
/*
  Because we are flatting the nested structure,
  we have to always start at index 1 to ensure we have the results,
  and then rely on todatasubset() to actually cast the results

  e.g. If batchsize is 3, and we are on page 2, and the data is:
  {
    {
      parent: {
        {
          child: {},
          child: {},
          child: {},
          child: {},
        }
      },
      parent: {
        {
          child: {},
          child: {},
        }
      }
    }
  }
  Then we would still to return parent 1, because child 4 is part of parent 1,
  and starting above index 1 would exclude parent 1
*/
  pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: ri!pagingInfo.startIndex + ri!pagingInfo.batchSize,
    sort: ri!pagingInfo.sort
  )
)

