/*This rule cannot have test cases as it returns a grid column*/
{
  #"SYSTEM_SYSRULES_gridColumn"(
    label: "Task Name",
    sortfield: "taskName",
    value: #"SYSTEM_SYSRULES_richTextDisplayField"(
      value: {
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: fv!row.taskName,
          link: #"SYSTEM_SYSRULES_startProcessLink"(
            processModel: rule!AS_GSS_TMG_UT_taskProcessAsLink(
              subTypeCode: fv!row.taskBehaviorType.behaviorSubtypeCode
            ),
            processParameters: { task: fv!row },
            showWhen: if(
              or(
                ri!disableLinks,
                rule!AS_GSS_TMG_UT_disableTaskActions(tasks: fv!row)
              ),
              false,
              rule!AS_GSS_TMG_UT_checkIfTaskHasUiForm(task: fv!row)
            )
          ),
          linkStyle: "STANDALONE",
          style: "STRONG"
        ),
        char(10),
        if(
          rule!AS_CO_UT_isBlank(fv!row.dueDate),
          {},
          a!localVariables(
            /*Extracting date from dueDate of type dateTime*/
            local!dueDate: todate(fv!row.dueDate),
            /*Getting today based on user's timezone*/
            local!today: rule!AS_CO_UT_returnTodayBasedOnInput(username: loggedInUser()),
            /*non-businesss days local variables*/
            /*calculating the end date diff from today*/
            local!endDateDifference: tointeger(local!dueDate - local!today),
            /*checking if alreadt expired*/
            local!expired: local!dueDate < local!today,
            /*checking if diff is 1 or 0 for duesoon status*/
            local!nonBusinessDueSoon: or(
              local!endDateDifference = 1,
              local!endDateDifference = 0
            ),
            /*business days local variables*/
            /*calculating the end date diff from today*/
            local!businessWorkingDays: calworkdays(local!today, local!dueDate),
            /*checking if diff is 1 or 0 for duesoon status*/
            local!businessDueSoon: or(
              local!businessWorkingDays - 1 = 0,
              local!businessWorkingDays - 1 = 1
            ),
            if(
              cons!AS_GSS_TMG_BOL_BUSINESS_DAYS_ONLY,
              /*Business days*/
              {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: {
                    #"SYSTEM_SYSRULES_richTextIcon"(
                      icon: if(
                        local!businessWorkingDays < 0,
                        "exclamation-circle",
                        local!businessDueSoon,
                        "clock-o",
                        local!businessWorkingDays > 0,
                        "circle-o",
                        null
                      ),
                      color: if(
                        local!businessWorkingDays < 0,
                        "NEGATIVE",
                        local!businessDueSoon,
                        "#FFC13E",
                        local!businessWorkingDays > 0,
                        "SECONDARY",
                        null
                      ),
                      altText: if(
                        local!businessWorkingDays < 0,
                        "Over due",
                        local!businessDueSoon,
                        "Due soon",
                        local!businessWorkingDays > 0,
                        "Pending",
                        null
                      )
                    ),
                    if(
                      local!businessWorkingDays < 0,
                      " OVERDUE",
                      local!businessDueSoon,
                      " Due soon",
                      local!businessWorkingDays > 0,
                      " Pending",
                      null
                    )
                  },
                  color: if(
                    local!businessWorkingDays < 0,
                    "NEGATIVE",
                    null
                  )
                )
              },
              /*Non Business days*/
              {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: {
                    #"SYSTEM_SYSRULES_richTextIcon"(
                      icon: if(
                        local!expired,
                        "exclamation-circle",
                        local!nonBusinessDueSoon,
                        "clock-o",
                        and(
                          local!endDateDifference > 0,
                          local!dueDate > local!today
                        ),
                        "circle-o",
                        null
                      ),
                      color: if(
                        local!expired,
                        "NEGATIVE",
                        local!nonBusinessDueSoon,
                        "#FFC13E",
                        and(
                         local!dueDate > local!today
                        ),
                        "SECONDARY",
                        null
                      ),
                      altText: if(
                        local!expired,
                        "Over due",
                        local!nonBusinessDueSoon,
                        "Due soon",
                        and(
                          local!endDateDifference > 0,
                          todate(local!dueDate) > local!today
                        ),
                        "Pending",
                        null
                      )
                    ),
                    if(
                      local!expired,
                      " OVERDUE",
                      local!nonBusinessDueSoon,
                      " Due soon",
                      and(
                        local!endDateDifference > 0,
                        todate(local!dueDate) > local!today
                      ),
                      " Pending",
                      null
                    )
                  },
                  color: if(local!expired, "NEGATIVE", null)
                )
              }
            )
          )
        )
      }
    ),
    width: "3X"
  )
}

