  local!loggedInUser: loggedInUser(),
  local!criterias: rule!AS_GSS_QE_getEvaluationCriteria(
    additionalFilters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: "evaluation.evaluationId",
        operator: "=",
        value: ri!evaluationId
      ),
      #"SYSTEM_SYSRULES_queryFilter"(
        field: "isActive",
        operator: "=",
        value: true
      )
    },
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  local!evaluation: rule!AS_GSS_QE_getEvaluation(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    evaluationId: ri!evaluationId
  ),
  /* To check if the user is CO/ EC to show all tasks */
  local!isShowAllTasks: or(
    local!loggedInUser = local!evaluation.contractingOfficer.username,
    local!loggedInUser = local!evaluation.contractingSpecialist.username,
    local!loggedInUser = local!evaluation.evaluationChief.username
  ),
  /* To check if the user is FC / Evaluator member and a chair of any factor */
  local!isFactorChair: and(
    contains(
      local!criterias.criteriaChair.username,
      tostring(local!loggedInUser)
    )
  ),
  #"SYSTEM_SYSRULES_queryLogicalExpression"(
    operator: "OR",
    logicalExpressions: {
      /* All users should see the tasks assigned to them */
      #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "userAssignee",
            operator: "=",
            value: local!loggedInUser
          )
        }
      ),
      if(
        local!isShowAllTasks,
        /* If CO/EC, then show all tasks assigned to them (previous QLE) and all tasks associated to the evaluation (additional QLE) */
        #"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator: "AND",
          filters: {
            #"SYSTEM_SYSRULES_queryFilter"(
              field: "evaluation.evaluationId",
              operator: "=",
              value: ri!evaluationId
            )
          }
        ),
        if(
          local!isFactorChair,
          /* If FC, then show all tasks assigned to them (previous QLE) and all tasks associated to the criteria they're assigned to (additional QLE) */
          #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "AND",
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: "criteria.criteriaChair.username",
                operator: "=",
                value: local!loggedInUser
              )
            }
          ),
          /* Else they're an evaluator, and should only see the tasks assigned to them (previous QLE) */
          {}
        )
      )
    }
  )
)

