with(
  local!nestedCdt: if(
    ri!index = 1,
    ri!cdt,
    rule!AS_CO_UT_zINTERNAL_indexPath_separateFieldHandler(
      cdt: ri!cdt,
      fields: ri!fieldsAndIndices[enumerate(ri!index - 1) + 1]
    )
  ),

  /*Have to handle differently for a dictionary/map or list of dictionary/map, or if the field itself is an array*/
  local!nestedCdtUpdate: if(
    and(
      rule!AS_CO_UT_isBlank(local!nestedCdt),
      or(
        rule!AS_CO_UT_typeCompare(
          value1: ri!cdt,
          value2: rule!AS_CO_UT_returnNullDictionary()
        ),
        rule!AS_CO_UT_typeCompare(
          value1: ri!cdt,
          value2: rule!AS_CO_UT_returnNullDictionaryList()
        ),
        rule!AS_CO_UT_typeCompare(
          value1: ri!cdt,
          value2: a!map()
        ),
        rule!AS_CO_UT_typeCompare(
          value1: ri!cdt,
          value2: {a!map()}
        )
      )
    ),
    if(
      /*Have to handle differently if passing indices through from AS_CO_UT_updateCdtFields*/
      and(
        rule!AS_CO_UT_isNotBlank(ri!indices),
        ri!index = 1
      ),
      rule!AS_CO_UT_returnNullDictionaryList(),
      rule!AS_CO_UT_returnNullDictionary()
    ),
    local!nestedCdt
  ),

  rule!AS_CO_UT_zINTERNAL_updateCdtFields_handler(
    cdt: local!nestedCdtUpdate,
    field: ri!fieldsAndIndices[ri!index],
/*Indices is only used when passing through from AS_CO_UT_updateCdtFields, never AS_CO_UT_updateCdtFieldsByPath*/
    indices: if(ri!index = 1, ri!indices, {}),
    newValue: ri!previous
  )
)

