  rule!AS_CO_UT_isBlank(ri!taskRefs),
  toboolean({}),
  rule!AS_CO_UT_isBlank(ri!taskRefs.taskName),
  repeat(length(ri!taskRefs), false),
  a!localVariables(
    local!existingRefTasksWithTheseNames: rule!AS_GSS_TMG_QE_getTaskRefs(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      taskName: ri!taskRefs.taskName,
      configurationLevelCode: ri!configurationLevelCode
    ),
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!taskRefs,
      expression: and(
        length(
          rule!AS_CO_UT_rejectCdtByField(
            cdt: local!existingRefTasksWithTheseNames,
            field: "taskRefId",
            value: fv!item.taskRefId
          )
        ) > 0,
        if(
          contains(
            lower(
              local!existingRefTasksWithTheseNames.taskName
            ),
            lower(fv!item.taskName)
          ),
          true,
          false
        )
      )
    )
  )
)

