if(
  /*if both the base and new vendors are blank, no vendors before so no vendors after*/
  and(
    rule!AS_CO_UT_isBlank(rule!AS_CO_UT_replaceNull(nullableValue: ri!baseEvaluationVendors, replacementValue:"")),
    rule!AS_CO_UT_isBlank(rule!AS_CO_UT_replaceNull(nullableValue: ri!updatedEvaluationVendors,replacementValue: ""))
  ),
  {},
  a!flatten(
    if(
      rule!AS_CO_UT_isBlank(ri!baseEvaluationVendors),
      /*If there were no vendors before the cancel, there should be none after*/
      #"SYSTEM_SYSRULES_forEach"(
        items: ri!updatedEvaluationVendors,
        expression: rule!AS_CO_UT_updateCdtFields(
          cdt: fv!item,
          fields: "isActive",
          newValues: false
        )
      ),
      if(
        rule!AS_CO_UT_isBlank(ri!updatedEvaluationVendors),
        /*if there were vendors before the cancel, they should be reactivated*/
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!baseEvaluationVendors,
          expression: rule!AS_CO_UT_updateCdtFields(
            cdt: fv!item,
            fields: "isActive",
            newValues: true
          )
        ),
        a!localVariables(
          /*Get list of vendor Ids*/
          local!distinctVendorIds: union(ri!baseEvaluationVendors.vendorId,ri!updatedEvaluationVendors.vendorId),
          #"SYSTEM_SYSRULES_forEach"(
            items: local!distinctVendorIds,
            expression: a!localVariables(
              /*Get the original eval vendor data*/
              local!baseVendor: rule!AS_CO_UT_filterCdtByField(
                cdt: ri!baseEvaluationVendors,
                field: "vendorId",
                value: fv!item
              ),
              /*Get the updated eval vendor data*/
              local!incomingVendor: rule!AS_CO_UT_filterCdtByField(
                cdt: ri!updatedEvaluationVendors,
                field: "vendorId",
                value: fv!item
              ),
              if(
                /*If no original vendor, soft delete newest vendor*/
                rule!AS_CO_UT_isBlank(local!baseVendor),
                rule!AS_CO_UT_updateCdtFields(
                  cdt: local!incomingVendor,
                  fields: {"isActive"},
                  newValues: {false}
                ),
                /*Default, take the base/original values since they will be active and have unchanged submission date*/
                local!baseVendor
              )
            )
          )
        )
      )
    )
  )
 )

