if(
  rule!AS_CO_UT_isBlank(ri!dueDate),
  "",
  if(
    cons!AS_GSS_BOL_BUSINESS_DAYS_ONLY,
    a!localVariables(
      /*Getting today based on user's timezone*/
      local!today: rule!AS_CO_UT_returnTodayBasedOnInput(username: loggedInUser()),
      /*calculating the due date diff from today*/
      local!dueDateDifference: tointeger(
        calworkdays(todate(local!today), ri!dueDate)
      ),
      /*Checking if the due date is in past (expired) and active*/
      local!expired: todate(ri!dueDate) < local!today,
      /*calculating the expired duration from today*/
      local!expiredDuration: if(
        local!expired,
        tointeger(
          calworkdays(todate(ri!dueDate), local!today)
        ),
        ""
      ),
      /*checking if expiring in 1/0 days from today*/
      /*Including - 1 for calculation when only business days are considered ( this is because calworkdays() returns the no of days including today*/
      local!dueSoon: or(
        local!dueDateDifference - 1 = 1,
        local!dueDateDifference - 1 = 0
      ),
      /*checking if expiring more than due soon from today*/
      local!expiresinMoreThanDueSoon: tointeger(
        calworkdays(local!today, todate(ri!dueDate))
      ),
      {
        #"SYSTEM_SYSRULES_richTextIcon"(
          icon: "clock-o",
          size: "SMALL",
          color: if(
            local!expired,
            "NEGATIVE",
            if(local!dueSoon, "#FFC13E", "")
          ),
          showWhen: rule!AS_CO_UT_isNotBlank(todate(ri!dueDate))
        ),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: if(
            rule!AS_CO_UT_isBlank(todate(ri!dueDate)),
            "",
            if(
              local!expired,
              /*Including - 1 for expired duration calculation when only business days are considered ( this is because calworkdays() returns the no of days including today*/
              " Expired " & local!expiredDuration - 1 & " days ago",
              /*Including - 1 for expired duration calculation when only business days are considered ( this is because calworkdays() returns the no of days including today*/
              " Expires in " & local!expiresinMoreThanDueSoon - 1 & " days"
            )
          ),
          color: if(
            rule!AS_CO_UT_isBlank(todate(ri!dueDate)),
            "",
            local!expired,
            "NEGATIVE",
            ""
          )
        )
      }
    ),
    a!localVariables(
      /*Getting today based on user's timezone*/
      local!today: rule!AS_CO_UT_returnTodayBasedOnInput(username: loggedInUser()),
      /*calculating the due date diff from today*/
      local!dueDateDifference: tointeger(todate(ri!dueDate) - local!today),
      /*Checking if the due date is in past (expired)*/
      local!expired: todate(ri!dueDate) < local!today,
      /*calculating the expired duration from today*/
      local!expiredDuration: tointeger(local!today - todate(ri!dueDate)),
      /*checking if expiring in 1 / 0 from today*/
      local!dueSoon: or(
        local!dueDateDifference = 0,
        local!dueDateDifference = 1
      ),
      /*checking if expiring more than due soon from today*/
      local!expiresinMoreThanDueSoon: tointeger(todate(ri!dueDate) - local!today),
      {
        #"SYSTEM_SYSRULES_richTextIcon"(
          icon: "clock-o",
          size: "SMALL",
          color: if(
            local!expired,
            "NEGATIVE",
            if(local!dueSoon, "#FFC13E", {})
          ),
          showWhen: rule!AS_CO_UT_isNotBlank(todate(ri!dueDate))
        ),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: if(
            rule!AS_CO_UT_isBlank(todate(ri!dueDate)),
            {},
            if(
              local!expired,
              " Expired " & local!expiredDuration & " days ago",
              " Expires in " & local!expiresinMoreThanDueSoon & " days"
            )
          ),
          color: if(
            rule!AS_CO_UT_isBlank(todate(ri!dueDate)),
            {},
            local!expired,
            "NEGATIVE",
            {}
          )
        )
      }
    )
  )
)

