a!localVariables(
  /*Task name for Review groups*/
  local!templateTaskAudit: rule!AS_GSS_TMG_QE_getTemplateTaskAudits(
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "templateTaskAuditId",
      ascending: false
    ),
    templateTaskId: ri!itemId,
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT
  ).simpleFieldChanges,
  local!reviewGroupName: if(
    rule!AS_CO_UT_isBlank(
      if(
        rule!AS_CO_UT_isBlank(ri!templateTaskRef.groupAssignee),
        togroup(
          tostring(
            rule!AS_CO_UT_filterCdtByField(
              cdt: local!templateTaskAudit,
              field: "fieldName",
              value: "groupAssignee"
            ).oldValue
          )
        ),
        togroup(ri!templateTaskRef.groupAssignee)
      )
    ),
    null,
    group(
      if(
        rule!AS_CO_UT_isBlank(ri!templateTaskRef.groupAssignee),
        togroup(
          tostring(
            rule!AS_CO_UT_filterCdtByField(
              cdt: local!templateTaskAudit,
              field: "fieldName",
              value: "groupAssignee"
            ).oldValue
          )
        ),
        togroup(ri!templateTaskRef.groupAssignee)
      ),
      "groupName"
    )
  ),
  local!reviewGroupSuffix: {
    " ",
    rule!AS_GAM_CO_I18N_UT_displayLabel(
      bundle: ri!i18nData,
      bundleKey: if(
        ri!templateAuditEntry.actionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_ADD,
        "txt_ToReviewGrp",
        "txt_OfReviewGrp"
      )
    ),
    " "
  },
  local!reviewSuffix: {
    " ",
    rule!AS_GAM_CO_I18N_UT_displayLabel(
      bundle: ri!i18nData,
      bundleKey: if(
        ri!templateAuditEntry.actionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_ADD,
        "txt_ToReview",
        "txt_OfReview"
      )
    ),
    " "
  },
  choose(
    rule!AS_CO_UT_chooseWherecontainsHelper(
      value: ri!fieldName,
      inArray: rule!AS_GSS_TMG_CONS_templateAuditFields()
    ),
    /*Default*/
    {},
    /*Note: the order should be similar to AS_GSS_TMG_CONS_templateAuditFields*/
    /*Review Name*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "the review"
      ),
      " ",
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: ri!template.templateName,
        style: "STRONG"
      )
    },
    /*Review Description*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "the review description"
      ),
      " ",
      rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundle: ri!i18nData,
        bundleKey: if(
          ri!templateAuditEntry.actionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_ADD,
          "txt_ToReview",
          "txt_OfReview"
        )
      ),
      " ",
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: ri!template.templateName,
        style: "STRONG"
      )
    },
    /*Group Assignee*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "group assignee"
      ),
      local!reviewGroupSuffix,
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: local!reviewGroupName,
        style: "STRONG"
      )
    },
    /*Task Description*/
    {},
    /*Task*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: rule!AS_GAM_CO_I18N_UT_displayLabel(
          bundle: ri!i18nData,
          bundleKey: if(
            rule!AS_CO_UT_isBlank(value: local!reviewGroupName),
            "txt_AReviewGrp",
            "txt_ReviewGrp"
          )
        )
      ),
      " ",
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: local!reviewGroupName,
        style: "STRONG"
      )
    },
    /*Task ref id Precedent - for checklists*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "precedent"
      ),
      " ",
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: rule!AS_CO_UT_filterCdtByField(
          cdt: ri!taskRefData,
          field: "taskRefId",
          value: if(
            ri!templateAuditEntry.actionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_DELETE,
            ri!oldValue,
            ri!newValue
          )
        ).taskName,
        style: "STRONG"
      ),
      local!reviewGroupSuffix,
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: local!reviewGroupName,
        style: "STRONG"
      )
    },
    /*Group id Precedent - for Reviews*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "precedent" & " "
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: if(
          rule!AS_CO_UT_isBlank(
            togroup(
              if(
                ri!templateAuditEntry.actionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_DELETE,
                ri!oldValue,
                ri!newValue
              )
            )
          ),
          null,
          group(
            togroup(
              if(
                ri!templateAuditEntry.actionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_DELETE,
                ri!oldValue,
                ri!newValue
              )
            ),
            "groupName"
          )
        ),
        style: "STRONG"
      ),
      local!reviewGroupSuffix,
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: local!reviewGroupName,
        style: "STRONG"
      )
    },
    /*Duration*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "duration"
      ),
      local!reviewGroupSuffix,
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: local!reviewGroupName,
        style: "STRONG"
      )
    },
    /*Duration unit*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "duration unit"
      ),
      local!reviewGroupSuffix,
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: local!reviewGroupName,
        style: "STRONG"
      )
    },
    /*No of days*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "days from start"
      ),
      local!reviewGroupSuffix,
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: local!reviewGroupName,
        style: "STRONG"
      )
    },
    /*Operation*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "operation"
      ),
      local!reviewSuffix,
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: ri!template.templateName,
        style: "STRONG"
      )
    },
    /*Threshold Amount*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "threshold amount"
      ),
      local!reviewSuffix,
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: ri!template.templateName,
        style: "STRONG"
      )
    },
    /*Document Template*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "document template"
      ),
      local!reviewSuffix,
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: ri!template.templateName,
        style: "STRONG"
      )
    }
  )
)

