#"SYSTEM_SYSRULES_forEach"(
  items: ri!selectedReview.templateTasks,
  expression: a!save(
    fv!item.templateTaskPrecedents,
    a!localVariables(
      local!templateTask: fv!item,
      local!choices: rule!AS_CO_UT_rejectNullValuesFromArray(
        rule!AS_CO_UT_rejectCdtByField(
          cdt: ri!selectedReview.templateTasks,
          field: "groupAssignee",
          value: fv!item.groupAssignee
        ).groupAssignee
      ),
      /*checking if the groupId_precedent doesnot exist in the choice values, the corresponding precedent will be removed*/
      if(
        rule!AS_CO_UT_isBlank(
          local!templateTask.templateTaskPrecedents
        ),
        null,
        a!flatten(
          #"SYSTEM_SYSRULES_forEach"(
            items: local!templateTask.templateTaskPrecedents,
            expression: if(
              rule!AS_CO_UT_not(
                contains(
                  tointeger(local!choices),
                  fv!item.groupId_precedent
                )
              ),
              {},
              fv!item
            )
          )
        )
      )
    )
  )
)

