#"SYSTEM_SYSRULES_gridColumn"(
  label: rule!AS_GAM_CO_I18N_UT_displayLabel(
    bundle: ri!i18nData,
    bundleKey: ri!bundleKey
  ),
  value: #"SYSTEM_SYSRULES_richTextDisplayField"(
    value: a!localVariables(
      local!docExtension: rule!AS_CO_UT_documentProperty(
        document: rule!AS_CO_UT_indexPath(cdt: fv!row, field: ri!docIdFieldName),
        property: "extension"
      ),
      local!docName: rule!AS_CO_UT_formatDocumentAndExtension(
        documentName: rule!AS_CO_UT_indexPath(cdt: fv!row, field: ri!docNameFieldName),
        extension: local!docExtension
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: {
          rule!AS_CO_UI_createDocumentIcon(
            extension: local!docExtension,
            size: "MEDIUM_PLUS"
          ),
          " ",
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: rule!AS_CO_UT_replaceNull(
              nullableValue: local!docName,
              replacementValue: "-"
            )
          )
        },
        link: if(
          ri!showDeleteAction = true,
          {},
          #"SYSTEM_SYSRULES_dynamicLink"(
            value: rule!AS_CO_UT_indexPath(
              cdt: fv!row,
              field: ri!documentIdFieldName
            ),
            saveInto: {
              ri!selectedDocumentId,
              ri!additionalSave
            }
          )
        ),
        linkStyle: "STANDALONE",
        style: "STRONG"
      )
    )
  ),
  sortField: ri!docNameFieldName
)

