data.sail_code:
a!localVariables(
  /* Integration to get vendor details*/
  local!vendorData: if(
    and(
      rule!AS_CO_UT_isBlank(ri!cage),
      rule!AS_CO_UT_isBlank(ri!uniqueEntityId)
    ),
    null,
    rule!AS_GAM_GetVendorDetailsFromSAMGov(
      cage: ri!cage,
      uniqueEntityId: ri!uniqueEntityId
    )
  ),
  /* Constructing a dictionary to map based on the data returned*/
  if(
    or(
      rule!AS_CO_UT_isBlank(local!vendorData.result),
      rule!AS_CO_UT_isBlank(
        rule!AS_GSS_UT_deepIndex(
          item: local!vendorData,
          indexArray: { "result", "body", "entityData" }
        )
      )
    ),
    /* If no data is retrieved from Integration*/
    a!map(
      data: null,
      error: if(
        rule!AS_CO_UT_isBlank(local!vendorData.error),
        cons!AS_GSS_TXT_NO_RESULTS_FOUND_FROM_SAM_GOV,
        local!vendorData.error
      )
    ),
    /* When data is retrieved */
    a!map(
      data: #"SYSTEM_SYSRULES_forEach"(
        items: local!vendorData.result.body.entityData,
        expression: /* Updating the vendor fields with data from the integration. */
        recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD(
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.vendorRefId: ri!refVendor[recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.vendorRefId],
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.legalName: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: "entityRegistration.legalBusinessName"
          ),
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.businessName: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: "entityRegistration.dbaName"
          ),
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.refVendorBusinessTypes: rule!AS_GSS_populateBusinessTypeFromSAMGovForRefVendorRecord(
            businessTypes: rule!AS_CO_UT_indexPath(
              cdt: fv!item,
              field: "coreData.businessTypes.businessTypeList.businessTypeCode"
            )
          ),
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.vendorAddressLine1: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: {
              "coreData.physicalAddress.addressLine1"
            }
          ),
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.vendorAddressLine2: null,
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.vendorCity: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: { "coreData.physicalAddress.city" }
          ),
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.state: rule!AS_GSS_populateStateFromSAMGovForRefVendorRecord(
            stateCode: rule!AS_CO_UT_indexPath(
              cdt: fv!item,
              field: {
                "coreData.physicalAddress.stateOrProvinceCode"
              }
            )
          ),
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.country: rule!AS_GSS_populateCountryFromSAMGovForRefVendorRecord(
            countryCode: rule!AS_CO_UT_indexPath(
              cdt: fv!item,
              field: { "coreData.physicalAddress.countryCode" }
            )
          ),
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.vendorZipCode: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: { "coreData.physicalAddress.zipCode" }
          ),
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.vendorZipCodeExt: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: {
              "coreData.physicalAddress.zipCodePlus4"
            }
          ),
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.vendorForeignPostalCode: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: { "coreData.physicalAddress.countryCode" }
          ),
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.cageCode: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: "entityRegistration.cageCode"
          ),
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.status: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: "entityRegistration.ueiStatus"
          ),
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.expirationDate: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: "entityRegistration.registrationExpirationDate"
          ),
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.isActive: true(),
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.sourceApplicationId: cons!AS_GSS_REF_ID_SOURCE_APPLICATION_GSS,
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.uniqueEntityId: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: "entityRegistration.ueiSAM"
          ),
          recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.federalIdentifier: concat(
            rule!AS_CO_UT_replaceNull(
              nullableValue: upper(
                stripwith(
                  rule!AS_CO_UT_indexPath(
                    cdt: fv!item,
                    field: "entityRegistration.legalBusinessName"
                  ),
                  " "
                )
              ),
              replacementValue: "N/A"
            ),
            "|",
            rule!AS_CO_UT_replaceNull(
              nullableValue: upper(
                rule!AS_CO_UT_indexPath(
                  cdt: fv!item,
                  field: "entityRegistration.ueiSAM"
                )
              ),
              replacementValue: "N/A"
            ),
            "|",
            rule!AS_CO_UT_replaceNull(
              nullableValue: upper(
                rule!AS_CO_UT_indexPath(
                  cdt: fv!item,
                  field: "entityRegistration.cageCode"
                )
              ),
              replacementValue: "N/A"
            )
          )
        )
      ),
      error: null
    )
  )
)

