data.sail_code:
if(
  /*Check there is at least one task*/
  rule!AS_CO_UT_isBlank(ri!tasksToAdd),
  "You must add at least one task",
  rule!AS_CO_UT_distinct(
    reject(
      rule!AS_CO_UT_isBlank(_),
      {
        touniformstring(
          /*Check required fields*/
          a!localVariables(
            local!requiredFields: rule!AS_CO_UT_filterCdtByField(
              cdt: rule!AS_GSS_TMG_BL_importTasksFromExcelFieldsList(),
              field: "required",
              value: true
            ),
            if(
              any(
                rule!AS_CO_UT_isBlank(_),
                a!flatten(
                  #"SYSTEM_SYSRULES_forEach"(
                    items: local!requiredFields.field,
                    expression: rule!AS_CO_UT_indexPath(cdt: ri!tasksToAdd, field: fv!item)
                  )
                )
              ),
              rule!AS_GAM_CO_I18N_UT_displayLabel(
                bundle: ri!i18nData,
                bundleKey: "vld_RequiredFields",
                arguments: rule!AS_CO_UT_separateListItemsInSentence(
                  list: local!requiredFields.label,
                  finalSeparator: lower(
                    "and"
                  )
                )
              ),
              ""
            )
          ),
          /*Check for Attach document required fields, if relevant*/
          #"SYSTEM_SYSRULES_forEach"(
            items: rule!AS_CO_UT_filterCdtByField(
              cdt: ri!tasksToAdd,
              field: "taskBehaviorType.behaviorSubtypeCode",
              value: {
                cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_DOCUMENT_UPLOAD
              }
            ),
            expression: if(
              rule!AS_CO_UT_isBlank(
                index(
                  fv!item.docUploadContext,
                  "docTypeId",
                  null
                )
              ),
              concat(
                "The following fields are required for",
                " ",
                rule!AS_CO_UT_displayRefDataLabelSingle(
                  singleData: fv!item.taskBehaviorType,
                  labelField: "behaviorDisplayName",
                  i18nData: ri!i18nData
                ),
                ": ",
                rule!AS_CO_UT_separateListItemsInSentence(
                  list: rule!AS_CO_UT_filterCdtByField(
                    cdt: rule!AS_GSS_TMG_BL_importTasksFromExcelFieldsList(),
                    field: "field",
                    value: "docUploadContext.docTypeId.refLabel"
                  ).label,
                  finalSeparator: lower(
                    "and"
                  )
                )
              ),
              {}
            )
          ),
          /*Check for Attach document with template fields required fields, if relevant*/
          #"SYSTEM_SYSRULES_forEach"(
            items: rule!AS_CO_UT_filterCdtByField(
              cdt: ri!tasksToAdd,
              field: "taskBehaviorType.behaviorSubtypeCode",
              value: {
                cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_DOCUMENT_WITH_TEMPLATE
              }
            ),
            expression: if(
              or(
                rule!AS_CO_UT_isBlank(
                  index(
                    fv!item.docUploadContext,
                    "docTypeId",
                    null
                  )
                ),
                rule!AS_CO_UT_isBlank(
                  index(
                    fv!item.docUploadContext,
                    "docTemplate",
                    null
                  )
                )
              ),
              concat(
                "The following fields are required for",
                " ",
                rule!AS_CO_UT_displayRefDataLabelSingle(
                  singleData: fv!item.taskBehaviorType,
                  labelField: "behaviorDisplayName",
                  i18nData: ri!i18nData
                ),
                ": ",
                rule!AS_CO_UT_separateListItemsInSentence(
                  list: rule!AS_CO_UT_filterCdtByField(
                    cdt: rule!AS_GSS_TMG_BL_importTasksFromExcelFieldsList(),
                    field: "field",
                    value: {
                      "docUploadContext.docTypeId.refLabel",
                      "docUploadContext.docTemplate.documentName"
                    }
                  ).label,
                  finalSeparator: lower(
                    "and"
                  )
                )
              ),
              {}
            )
          ),
          /*Check name length*/
          #"SYSTEM_SYSRULES_forEach"(
            items: ri!tasksToAdd,
            expression: rule!AS_CO_VD_textValidation(
              value: fv!item.taskName,
              maxLength: 50,
              maxLengthMessage: "A task name cannot be more than 50 characters"
            )
          ),
          /*Check name duplication*/
          if(
            or(
              or(
                rule!AS_GSS_TMG_UT_isDuplicateTaskReferenceName(
                  taskRefs: ri!tasksToAdd,
                  configurationLevelCode: cons!AS_GSS_TMG_ENUM_TASK_CONFIGURATION_LEVEL_CODE_AD_HOC
                )
              ),
              rule!AS_CO_UT_checkIfDuplicateExistsInArray(
                array: ri!tasksToAdd.taskName,
                caseSensitive: false,
                skipNulls: true
              )
            ),
            "You cannot have two tasks with the same name",
            ""
          )
        )
      }
    )
  )
)

data.definition:
if(
  /*Check there is at least one task*/
  rule!AS_CO_UT_isBlank(ri!tasksToAdd),
  "You must add at least one task",
  rule!AS_CO_UT_distinct(
    reject(
      rule!AS_CO_UT_isBlank(_),
      {
        touniformstring(
          /*Check required fields*/
          a!localVariables(
            local!requiredFields: rule!AS_CO_UT_filterCdtByField(
              cdt: rule!AS_GSS_TMG_BL_importTasksFromExcelFieldsList(),
              field: "required",
              value: true
            ),
            if(
              any(
                rule!AS_CO_UT_isBlank(_),
                a!flatten(
                  #"SYSTEM_SYSRULES_forEach"(
                    items: local!requiredFields.field,
                    expression: rule!AS_CO_UT_indexPath(cdt: ri!tasksToAdd, field: fv!item)
                  )
                )
              ),
              rule!AS_GAM_CO_I18N_UT_displayLabel(
                bundle: ri!i18nData,
                bundleKey: "vld_RequiredFields",
                arguments: rule!AS_CO_UT_separateListItemsInSentence(
                  list: local!requiredFields.label,
                  finalSeparator: lower(
                    "and"
                  )
                )
              ),
              ""
            )
          ),
          /*Check for Attach document required fields, if relevant*/
          #"SYSTEM_SYSRULES_forEach"(
            items: rule!AS_CO_UT_filterCdtByField(
              cdt: ri!tasksToAdd,
              field: "taskBehaviorType.behaviorSubtypeCode",
              value: {
                cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_DOCUMENT_UPLOAD
              }
            ),
            expression: if(
              rule!AS_CO_UT_isBlank(
                index(
                  fv!item.docUploadContext,
                  "docTypeId",
                  null
                )
              ),
              concat(
                "The following fields are required for",
                " ",
                rule!AS_CO_UT_displayRefDataLabelSingle(
                  singleData: fv!item.taskBehaviorType,
                  labelField: "behaviorDisplayName",
                  i18nData: ri!i18nData
                ),
                ": ",
                rule!AS_CO_UT_separateListItemsInSentence(
                  list: rule!AS_CO_UT_filterCdtByField(
                    cdt: rule!AS_GSS_TMG_BL_importTasksFromExcelFieldsList(),
                    field: "field",
                    value: "docUploadContext.docTypeId.refLabel"
                  ).label,
                  finalSeparator: lower(
                    "and"
                  )
                )
              ),
              {}
            )
          ),
          /*Check for Attach document with template fields required fields, if relevant*/
          #"SYSTEM_SYSRULES_forEach"(
            items: rule!AS_CO_UT_filterCdtByField(
              cdt: ri!tasksToAdd,
              field: "taskBehaviorType.behaviorSubtypeCode",
              value: {
                cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_DOCUMENT_WITH_TEMPLATE
              }
            ),
            expression: if(
              or(
                rule!AS_CO_UT_isBlank(
                  index(
                    fv!item.docUploadContext,
                    "docTypeId",
                    null
                  )
                ),
                rule!AS_CO_UT_isBlank(
                  index(
                    fv!item.docUploadContext,
                    "docTemplate",
                    null
                  )
                )
              ),
              concat(
                "The following fields are required for",
                " ",
                rule!AS_CO_UT_displayRefDataLabelSingle(
                  singleData: fv!item.taskBehaviorType,
                  labelField: "behaviorDisplayName",
                  i18nData: ri!i18nData
                ),
                ": ",
                rule!AS_CO_UT_separateListItemsInSentence(
                  list: rule!AS_CO_UT_filterCdtByField(
                    cdt: rule!AS_GSS_TMG_BL_importTasksFromExcelFieldsList(),
                    field: "field",
                    value: {
                      "docUploadContext.docTypeId.refLabel",
                      "docUploadContext.docTemplate.documentName"
                    }
                  ).label,
                  finalSeparator: lower(
                    "and"
                  )
                )
              ),
              {}
            )
          ),
          /*Check name length*/
          #"SYSTEM_SYSRULES_forEach"(
            items: ri!tasksToAdd,
            expression: rule!AS_CO_VD_textValidation(
              value: fv!item.taskName,
              maxLength: 50,
              maxLengthMessage: "A task name cannot be more than 50 characters"
            )
          ),
          /*Check name duplication*/
          if(
            or(
              or(
                rule!AS_GSS_TMG_UT_isDuplicateTaskReferenceName(
                  taskRefs: ri!tasksToAdd,
                  configurationLevelCode: cons!AS_GSS_TMG_ENUM_TASK_CONFIGURATION_LEVEL_CODE_AD_HOC
                )
              ),
              rule!AS_CO_UT_checkIfDuplicateExistsInArray(
                array: ri!tasksToAdd.taskName,
                caseSensitive: false,
                skipNulls: true
              )
            ),
            "You cannot have two tasks with the same name",
            ""
          )
        )
      }
    )
  )
)

