data.sail_code:
a!localVariables(
  local!itemName: if(
    rule!AS_CO_UT_isBlank(ri!templateTaskRef.taskRef.taskName),
    index(
      ri!taskRefData,
      wherecontains(
        tointeger(
          rule!AS_CO_UT_filterCdtByField(
            cdt: rule!AS_GSS_TMG_QE_getTemplateTaskAudits(
              sort: #"SYSTEM_SYSRULES_sortInfo"(
                field: "templateTaskAuditId",
                ascending: false
              ),
              templateTaskId: ri!itemId,
              returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT
            ).simpleFieldChanges,
            field: "fieldName",
            value: "taskRef.taskRefId"
          ).oldValue
        ),
        tointeger(index(ri!taskRefData, "taskRefId", null))
      ),
      null
    ).taskName,
    ri!templateTaskRef.taskRef.taskName
  ),
  local!itemSuffix: {
    " ",
    rule!AS_GAM_CO_I18N_UT_displayLabel(
      bundle: ri!i18nData,
      bundleKey: if(
        ri!templateAuditEntry.actionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_ADD,
        "txt_ToItem",
        "txt_OfItem"
      )
    ),
    " "
  },
  choose(
    rule!AS_CO_UT_chooseWherecontainsHelper(
      value: ri!fieldName,
      inArray: rule!AS_GSS_TMG_CONS_templateAuditFields()
    ),
    /*Note: the order should be similar to AS_GSS_TMG_CONS_templateAuditFields*/
    /*Default*/
    {},
    /*Template Name*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "the approach name"
      ),
      " ",
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: ri!template.templateName,
        style: "STRONG"
      )
    },
    /*Template Description*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "the approach description"
      ),
      if(
        ri!templateAuditEntry.actionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_ADD,
        " to the process ",
        " of the process "
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: ri!template.templateName,
        style: "STRONG"
      )
    },
    /*Group Assignee*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "group assignee"
      ),
      local!itemSuffix,
      #"SYSTEM_SYSRULES_richTextItem_v1"(text: local!itemName, style: "STRONG")
    },
    /*Task Description*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "the task description"
      ),
      local!itemSuffix,
      #"SYSTEM_SYSRULES_richTextItem_v1"(text: local!itemName, style: "STRONG")
    },
    /*Task*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: rule!AS_GAM_CO_I18N_UT_displayLabel(
          bundle: ri!i18nData,
          bundleKey: if(
            rule!AS_CO_UT_isBlank(value: local!itemName),
            "txt_AnItem",
            "txt_Item"
          )
        ) & " "
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(text: local!itemName, style: "STRONG")
    },
    /*Task ref id Precedent - for processes*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "precedent" & " "
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: rule!AS_CO_UT_filterCdtByField(
          cdt: ri!taskRefData,
          field: "taskRefId",
          value: if(
            ri!templateAuditEntry.actionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_DELETE,
            ri!oldValue,
            ri!newValue
          )
        ).taskName,
        style: "STRONG"
      ),
      local!itemSuffix,
      #"SYSTEM_SYSRULES_richTextItem_v1"(text: local!itemName, style: "STRONG")
    },
    /*Group id Precedent - will be null for process tasks*/
    {},
    /*Duration*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "duration"
      ),
      local!itemSuffix,
      #"SYSTEM_SYSRULES_richTextItem_v1"(text: local!itemName, style: "STRONG")
    },
    /*Duration unit*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "duration unit"
      ),
      local!itemSuffix,
      #"SYSTEM_SYSRULES_richTextItem_v1"(text: local!itemName, style: "STRONG")
    },
    /*No of days*/
    {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "days from start"
      ),
      local!itemSuffix,
      #"SYSTEM_SYSRULES_richTextItem_v1"(text: local!itemName, style: "STRONG")
    }
  )
)

