a!localVariables(
  local!httpResponseForResponses,
  local!httpResponseForJustifications,
  local!formattedText: rule!AS_GSS_BL_formatConsensusResponses_ToCombineResponsesByAI(
    consensusResponses: ri!selectedConsensusResponses
  ),
  {
    a!save(
      local!httpResponseForResponses,
      rule!AS_GSS_INT_summarize_ConsensusResponsesOrFinalComment(
        responseFormattedText: local!formattedText.responsesFormatted,
        prompt: cons!AS_GSS_COMBINE_CONSENSUS_RESPONSES_PROMPT
      )
    ),
    a!save(
      local!httpResponseForJustifications,
      rule!AS_GSS_INT_summarize_ConsensusResponsesOrFinalComment(
        responseFormattedText: local!formattedText.justificationFormatted,
        prompt: cons!AS_GSS_COMBINE_CONSENSUS_JUSTIFICATIONS_PROMPT,
        assistantText: index(
          rule!AS_GSS_UT_deepIndex(
            item: local!httpResponseForResponses,
            indexArray: { "result", "Response", "Chat Completions" }
          ),
          1,
          null
        )
      )
    ),
    if(
      and(
        toboolean(local!httpResponseForResponses.success),
        toboolean(
          local!httpResponseForJustifications.success
        )
      ),
      {
        a!save(
          ri!combinedConsensusResponse,
          #"urn:appian:function:v1:a:update"(
            ri!combinedConsensusResponse,
            {
              "response",
              "justification",
              "isOriginallyGeneratedByAi"
            },
            {
              index(
                rule!AS_GSS_UT_deepIndex(
                  item: local!httpResponseForResponses,
                  indexArray: { "result", "Response", "Chat Completions" }
                ),
                1,
                null
              ),
              index(
                rule!AS_GSS_UT_deepIndex(
                  item: local!httpResponseForJustifications,
                  indexArray: { "result", "Response", "Chat Completions" }
                ),
                1,
                null
              ),
              true()
            }
          )
        ),
        rule!AS_GSS_MTR_SAVE_combinedConsensusResponsesUsingConsensusAI()
      },
      {}
    ),
    #"SYSTEM_SYSRULES_writeToDataStoreEntity"(
      dataStoreEntity: cons!AS_GSS_ENT_CONSENSUS_RESP_AI_SUMMARY,
      valueToStore: #"SYSTEM_SYSRULES_forEach"(
        {
          local!httpResponseForResponses,
          local!httpResponseForJustifications
        },
        'type!{urn:com:appian:types:AS:GSS}AS_GSS_ConsensusResponseAiSummary'(
          consensusId: index(
            rule!AS_CO_UT_rejectNullValuesFromArray(
              array: ri!selectedConsensusResponses.consensusId
            ),
            1,
            null
          ),
          prompt: if(
            fv!isFirst,
            local!formattedText.responsesFormatted,
            local!formattedText.justificationFormatted
          ),
          response: if(
            a!isNullOrEmpty(fv!item.result),
            null,
            #"SYSTEM_SYSRULES_toJson_v1"(fv!item.result)
          ),
          errorInfo: if(
            a!isNullOrEmpty(index(fv!item, "error", null)),
            null,
            #"SYSTEM_SYSRULES_toJson_v1"(
              value: rule!AS_CO_UT_castToDictionary(value: index(fv!item, "error", null))
            )
          ),
          isSuccess: fv!item.success,
          createdBy: loggedInUser(),
          createdDateTime: now()
        )
      ),
      onSuccess: a!save(
        ri!consensusResponseAiIntegrationResults,
        fv!storedValues
      )
    )
  }
)

