cast(
   'type!{http://www.appian.com/ae/types/2009}Boolean',
  if(
    or(

      /*If index is less than 0*/
      tointeger(ri!index) <= 0,

      /*If index is greater than array length*/
      tointeger(ri!index) > count(ri!array),

      /*If we are skipping nulls, and the current index is null*/
      and(
        rule!AS_CO_UT_booleanDefaultFalse(ri!skipNulls),
        rule!AS_CO_UT_isBlank(ri!array[ri!index])
      )
    ),

    /*Then return false*/
    false,

    /*Otherwise, remove the current index from the list*/
    /*And check if the remaining list contains the current index*/
    with(
      local!array: remove(
        ri!array,
        tointeger(ri!index)
      ),
      local!valueAtIndex: ri!array[ri!index],
      if(
        ri!caseSensitive,
        contains(
          local!array,
          local!valueAtIndex
        ),
        contains(
          lower(local!array),
          lower(local!valueAtIndex)
        )
      )
    )
  )
)

