a!localVariables(
  local!username: rule!AS_CO_UT_replaceNull(
    nullableValue: ri!username,
    replacementValue: loggedInUser()
  ),
  local!isUserManager: rule!AS_GSS_TMG_UT_isUserManager(username: local!username),
  rule!AS_CO_UT_displayValue(
    value: ri!assigneeTypeFilter,
    inArray: rule!AS_GSS_TMG_CONS_ASSIGNEE_TYPE_FILTERS().value,
    replacement: {
      /* All -- only filter if we are applying some type of group filter */
      if(
        ri!applyGroupFilter,
        #"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator: "OR",
          logicalExpressions: {
            #"SYSTEM_SYSRULES_queryLogicalExpression"(
              operator: "AND",
              filters: {
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "groupAssignee",
                  operator: "in",
                  value: rule!AS_GSS_TMG_UT_returnUserGroupsForTaskFilters(username: local!username)
                ),
                /*Only restrict to unassigned if they are not a manager*/
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "userAssignee",
                  operator: "=",
                  value: " ",
                  applyWhen: not(local!isUserManager)
                )
              }
            ),
            #"SYSTEM_SYSRULES_queryLogicalExpression"(
              operator: "AND",
              filters: #"SYSTEM_SYSRULES_queryFilter"(
                field: "userAssignee",
                operator: "=",
                value: local!username
              )
            )
          }
        ),
        null
      ),
      /* Assigned to me */
      #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "userAssignee",
            operator: "=",
            value: local!username
          )
        }
      ),
      /* Unassigned */
      #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "groupAssignee",
            operator: "in",
            value: rule!AS_GSS_TMG_UT_returnUserGroupsForTaskFilters(username: local!username),
            applyWhen: ri!applyGroupFilter
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "userAssignee",
            operator: "=",
            value: " "
          )
        }
      )
    },
    customDefault: /*User assignee filter*/
    if(
      rule!AS_CO_UT_isNotBlank(ri!assigneeTypeFilter),
      #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        filters: #"SYSTEM_SYSRULES_queryFilter"(
          field: "userAssignee",
          operator: "=",
          value: ri!assigneeTypeFilter
        )
      ),
      {}
    )
  )
)

