if(
  cons!AS_GSS_TMG_BOL_BUSINESS_DAYS_ONLY,
  /*Business days*/
  a!localVariables(
    /*calculating the end date diff from today*/
    local!workingDays: if(rule!AS_CO_UT_isBlank(ri!dueDate),0,calworkdays(today(), ri!dueDate)),
    #"SYSTEM_SYSRULES_richTextDisplayField"(
      value: {
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: {
            if(
              local!workingDays < 0,
              #"SYSTEM_SYSRULES_richTextIcon"(
                icon: "exclamation-circle",
                color: "NEGATIVE",
                altText: "Over due"
              ),
              {}
            ),
            if(
              local!workingDays < 0,
              " " & local!workingDays,
              " " & local!workingDays - 1
            ) & "d"
          },
          color: if(local!workingDays < 0, "NEGATIVE", null)
        ),
        char(10),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: ri!dueDate,
          color: "SECONDARY",
          size: "SMALL"
        )
      }
    )
  ),
  /*Not Business days*/
  a!localVariables(
    /*calculating the end date diff from today*/
    local!endDateDifference: tointeger(todate(ri!dueDate) - today()),
    /*checking if alreadt expired*/
    local!expired: todate(min(ri!dueDate)) < today(),
    #"SYSTEM_SYSRULES_richTextDisplayField"(
      value: {
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: {
            if(
              local!expired,
              #"SYSTEM_SYSRULES_richTextIcon"(
                icon: "exclamation-circle",
                color: "NEGATIVE",
                altText: "Over due"
              ),
              {}
            ),
            if(
              local!expired,
              " " & local!endDateDifference & "d",
              or(
                local!endDateDifference = 1,
                local!endDateDifference = 0
              ),
              " " & local!endDateDifference & "d",
              and(
                local!endDateDifference > 0,
                todate(ri!dueDate) > today()
              ),
              " " & local!endDateDifference & "d",
              null
            )
          },
          color: if(local!expired, "NEGATIVE", null)
        ),
        char(10),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: ri!dueDate,
          color: "SECONDARY",
          size: "SMALL"
        )
      }
    )
  )
)

