a!localVariables(
  local!appianDocIdsToDelete: difference(
    tointeger(
      rule!AS_CO_UT_indexPath(
        cdt: ri!originalReferenceDocs,
        field: "appianDocId"
      )
    ),
    tointeger(
      rule!AS_CO_UT_indexPath(
        cdt: ri!referenceDocs,
        field: "appianDocId"
      )
    )
  ),
  local!updatedOriginalReferenceDocs: rule!AS_CO_UT_updateCdtFields(
    cdt: rule!AS_CO_UT_filterCdtByField(
      cdt: ri!originalReferenceDocs,
      field: "appianDocId",
      value: local!appianDocIdsToDelete
    ),
    fields: "isDeleted",
    newValues: true()
  ),
  local!updatedReferenceDocs: #"SYSTEM_SYSRULES_forEach"(
    items: ri!referenceDocs,
    expression: if(
      contains(
        tointeger(
          rule!AS_CO_UT_indexPath(
            cdt: ri!originalReferenceDocs,
            field: "appianDocId"
          )
        ),
        tointeger(fv!item.appianDocId)
      ),
      fv!item,
      rule!AS_CO_UT_updateCdtFields(
        cdt: fv!item,
        fields: { "documentName", "fileType", "docType" },
        newValues: {
          document(fv!item.appianDocId, "name"),
          document(fv!item.appianDocId, "extension"),
          rule!AS_GSS_QE_getRefDataByTypes(
            refDataIds: cons!AS_GSS_REF_ID_DOC_TYPE_REFERENCE
          )
        }
      )
    )
  ),
  union(
    local!updatedOriginalReferenceDocs,
    local!updatedReferenceDocs
  )
)

