        #"SYSTEM_SYSRULES_queryAggregationColumn"(
          field: "taskStatus.taskStatusId",
          alias: "taskStatusId",
          isGrouping: true
        ),
        #"SYSTEM_SYSRULES_queryAggregationColumn"(
          field: "taskId",
          alias: "taskCount",
          aggregationFunction: "COUNT"
        ),
        #"SYSTEM_SYSRULES_queryAggregationColumn"(
          field: "dueDate",
          alias: "dueDate",
          aggregationFunction: "MIN"
