with(
  local!typeNumber: rule!AS_CO_UT_returnTypeNumber(
    type: ri!type,
    typeOf: ri!typeOf
  ),

  /* If the object is empty, use null or {} depending on if it is array type or not */
  local!objectToCast: if(
    rule!AS_CO_UT_isNotBlank(ri!value),
    ri!value,
    rule!AS_CO_UT_checkIfSingleType(
      type: ri!type,
      typeOf: ri!typeOf
    ),
    null,
    {}
  ),

  cast(
    local!typeNumber,
    local!objectToCast
  )

)

