data.sail_code:
a!localVariables(
  local!i18nData: rule!AS_GSS_CO_UT_loadBundleFromFolder(langISOCode: null),
  /*Get the template to be used*/
  local!template: rule!AS_GSS_TMG_UT_getDefaultTemplate(),
  /*Convert the template tasks to runtime tasks*/
  /*Made refreshOnReferencedVarChange as false and saved the same in checklist dropdown (this has been made to auto-populate the userassigne column) */
  local!tasksToCreate: a!refreshVariable(
    value: rule!AS_GSS_TMG_CDT_mapReferenceTemplateToRuntimeTasks(
      template: local!template,
      calculateDueDate: true
    ),
    refreshOnReferencedVarChange: false
  ),
  local!initialTasksToCreateValue: a!refreshVariable(
    value: tostring(local!tasksToCreate),
    refreshOnReferencedVarChange: false
  ),
  #"SYSTEM_SYSRULES_formLayout_v1"(
    label: rule!AS_GAM_CO_I18N_UT_displayLabel(
      bundle: rule!AS_GSS_CO_UT_loadBundleFromFolder(langISOCode: null),
      bundleKey: "lbl_SetUp",
      arguments: "Approaches"
    ),
    contents: {
      rule!AS_GSS_TMG_CPS_processSetup(
        tasksToCreate: local!tasksToCreate,
        template: local!template,
        templateTypeId: ri!templateTypeId,
        evaluation: ri!evaluation,
        i18nData: local!i18nData
      ),
      #"SYSTEM_SYSRULES_sectionLayout_v1"(
        validations: {
          #"SYSTEM_SYSRULES_validationMessage"(
            message: "You cannot submit without an approach selected",
            validateAfter: "SUBMIT",
            showWhen: rule!AS_CO_UT_isBlank(local!template.templateId)
          ),
          #"SYSTEM_SYSRULES_validationMessage"(
            message: "You cannot submit without any tasks",
            validateAfter: "SUBMIT",
            showWhen: rule!AS_CO_UT_isBlank(local!tasksToCreate)
          )
        },
        validationGroup: "SETUPCHECKLIST"
      )
    },
    buttons: rule!AS_CO_CPS_cancelSaveButtons(
      cancelAccessibilityText: "Press Enter to cancel changes",
      confirmAccessibilityText: "Press Enter to set up checklist",
      confirmLabel: "Set up Approach",
      cancelLabel: "Cancel",
      confirmSaveInto: {
        a!save(
          ri!userAction,
          cons!AS_CO_ENUM_USER_ACTION_SUBMIT
        ),
        /*if(*/
          /*ri!templateTypeId = cons!AS_GSS_REF_ID_TEMPLATE_TYPE_CONTRACTING,*/
          /*a!save(*/
            /*ri!evaluation.templateId,*/
            /*local!template.templateId*/
          /*),*/
          /*{}*/
        /*),*/
        a!save(
          ri!tasksToCreate,
          rule!AS_GSS_TMG_UT_setTaskFieldsOnProcessSetupCompletion(
            tasksToCreate: local!tasksToCreate,
            evaluationId: ri!evaluation.evaluationId
          )
        ),
        a!save(
          ri!taskChecklist,
          rule!AS_GSS_TMG_CDT_mapRefTemplateToRuntimeChecklist(template: local!template)
        )
      },
      cancelSaveInto: {
        a!save(
          ri!userAction,
          cons!AS_CO_ENUM_USER_ACTION_CANCEL
        )
      },
      confirmValidationGroup: "SETUPCHECKLIST",
      confirmSubmit: true,
      cancelSubmit: true,
      cancelHeader: if(
        local!initialTasksToCreateValue <> tostring(local!tasksToCreate),
        "Unsaved Changes",
        {}
      ),
      cancelMessage: if(
        local!initialTasksToCreateValue <> tostring(local!tasksToCreate),
        "Are you sure you want to exit without saving?",
        {}
      )
    )
  )
)

a!localVariables(
  /*The button layout, which can dynamically be inside a section or not*/
  /*Note that when using this pattern of storing an interface as a local variable, it must be set to refreshAlways = true*/
  local!buttonLayout: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_ButtonLayout"(
      primaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          accessibilityText: ri!confirmAccessibilityText,
          label: ri!confirmLabel,
          size: ri!confirmSize,
          saveInto: if(
            rule!AS_CO_UT_isBlank(
              ri!confirmSaveInto
            ),
            {},
            ri!confirmSaveInto
          ),
          style: rule!AS_CO_UT_replaceNull(
            nullableValue: ri!confirmStyle,
            replacementValue: "PRIMARY"
          ),
          validate: true,
          validationGroup: ri!confirmValidationGroup,
          submit: ri!confirmSubmit,
          disabled: ri!confirmDisabled,
          confirmHeader: ri!confirmHeader,
          confirmMessage: ri!confirmMessage
        )
      },
      secondaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          accessibilityText: ri!cancelAccessibilityText,
          label: ri!cancelLabel,
          size: ri!cancelSize,
          saveInto: if(
            rule!AS_CO_UT_isBlank(
              ri!cancelSaveInto
            ),
            {},
            ri!cancelSaveInto
          ),
          style: rule!AS_CO_UT_replaceNull(
            nullableValue: ri!cancelStyle,
            replacementValue: "NORMAL"
          ),
          validate: false,
          submit: ri!cancelSubmit,
          disabled: ri!cancelDisabled,
          confirmHeader: ri!cancelHeader,
          confirmMessage: ri!cancelMessage,
          showWhen: ri!cancelShowWhen
        )
      },
      showWhen: ri!showWhen
    ),
    refreshAlways: true
  ),
  /*Components to show*/
  if(
    ri!includeDivider,
    #"SYSTEM_SYSRULES_sectionLayout_v1"(
      divider: "ABOVE",
      contents: {
        local!buttonLayout
      }
    ),
    local!buttonLayout
  )
)

