          ),
          refType: cons!AS_GSS_REF_TYPE_DOCUMENT_TYPE,
          value: ri!task.docUploadContext.docTypeId,
          saveInto: ri!task.docUploadContext.docTypeId,
          disabled: ri!disabled,
          placeholderLabel: "Select a document type",
          showWhen: if(
            or(
              cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_DOCUMENT_UPLOAD = ri!task.taskBehaviorType.behaviorSubtypeCode,
              cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_DOCUMENT_WITH_TEMPLATE = ri!task.taskBehaviorType.behaviorSubtypeCode
            ),
            true,
            false
          ),
          labelPosition: "ABOVE",
          required: true
        )
      }
    ),
    #"SYSTEM_SYSRULES_columnLayout"(
      contents: {
        rule!AS_CO_CP_dropdownField(
          label: "Document Template",
          labelPosition: "ABOVE",
          required: true,
          choiceLabels: local!documentTemplates.documentName,
          choiceValues: local!documentTemplates.documentTemplateId,
          value: ri!task.docUploadContext.docTemplate.documentTemplateId,
          saveInto: a!save(
            ri!task.docUploadContext.docTemplate,
            if(
              rule!AS_CO_UT_isBlank(save!value),
