          taskBehaviorTypes: local!taskBehaviorTypes,
          isDueDateRequired: ri!isDueDateRequired,
          disabled: ri!disabled
        )
      }
    )
  }
)

a!localVariables(
  local!taskCategories: rule!AS_GSS_TMG_QE_getTaskCategories(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  /*Will contain name of category that user wants to add tasks to */
  local!addingTaskCategory: rule!AS_CO_UT_initializeBlankLocalVariable(
    type: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_R_TaskCategory'
  ),
  local!triggerRefresh: now(),
  {
    if(
      rule!AS_CO_UT_isBlank(local!addingTaskCategory),
      {
        /*Display properties about the template*/
        rule!AS_GSS_TMG_SCT_processProperties(
          template: ri!template,
          i18nData: ri!i18nData,
          templateTypeId: ri!templateTypeId,
          tasksToCreate: ri!tasksToCreate,
          evaluation: ri!evaluation
        ),
        /*Grid per category*/
        #"SYSTEM_SYSRULES_forEach"(
          items: local!taskCategories,
          expression: rule!AS_GSS_TMG_CPS_manageRuntimeProcessCategory(
            i18nData: ri!i18nData,
            tasksToCreate: ri!tasksToCreate,
            taskCategory: fv!item,
            addingTaskCategory: local!addingTaskCategory,
            triggerRefresh: local!triggerRefresh,
            evaluation: ri!evaluation
          )
        )
      },
      /*add tasks to a category from catalog*/
      rule!AS_GSS_TMG_CPS_processSetupAddTaskToCategory(
        i18nData: ri!i18nData,
        addingTaskCategory: local!addingTaskCategory,
        tasksToCreate: ri!tasksToCreate,
        templateId: ri!template.templateId,
        evaluation: ri!evaluation
      )
    )
  }
)

a!localVariables(
  local!searchText: rule!AS_CO_UT_initializeBlankLocalVariable(
    type: 'type!{http://www.appian.com/ae/types/2009}Text'
  ),
  local!newCustomTask: rule!AS_CO_UT_initializeBlankLocalVariable(
    type: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_Task'
  ),
  local!customTasks: rule!AS_CO_UT_initializeBlankLocalVariable(
    type: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_Task?list'
  ),
  local!selectedTaskRefs: rule!AS_CO_UT_initializeBlankLocalVariable(
    type: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_R_TaskRef?list'
  ),
  if(
    rule!AS_CO_UT_isBlank(local!newCustomTask),
    /*Display of the task grids*/
    {
      /*Header*/
      #"SYSTEM_SYSRULES_sectionLayout_v1"(
        contents: rule!AS_CO_DSP_displayHeader(
          headerText: rule!AS_GAM_CO_I18N_UT_displayLabel(
            bundle: ri!i18nData,
            bundleKey: "lbl_SelectTasks",
            arguments: ri!addingTaskCategory.categoryName
          ),
          headerLevel: cons!AS_CO_ENUM_HEADER_LEVEL_ONE
        )
      ),
      /*Sub-header and search*/
      #"SYSTEM_SYSRULES_sectionLayout_v1"(
        contents: {
          #"SYSTEM_SYSRULES_columnsLayout"(
            columns: {
              #"SYSTEM_SYSRULES_columnLayout"(
                contents: rule!AS_CO_DSP_displayHeader(
                  headerText: "Preset Tasks",
                  headerLevel: cons!AS_CO_ENUM_HEADER_LEVEL_TWO
                )
              ),
              #"SYSTEM_SYSRULES_columnLayout"(
                contents: rule!AS_CO_CP_searchField(
                  value: local!searchText,
                  saveInto: local!searchText,
                  placeholder: rule!AS_GSS_TMG_pickerPlaceholder(
                    i18nData: ri!i18nData,
                    bundleKey: "lbl_Tasks"
                  )
                ),
                width: "MEDIUM"
              )
            },
            marginBelow: "NONE"
          ),
          /*Reference task grid*/
          rule!AS_GSS_TMG_GRD_referenceTaskGrid(
            allowLinkSelection: false,
            selectedTaskRefs: local!selectedTaskRefs,
            i18nData: ri!i18nData,
            allowCheckboxSelection: true,
            taskCategoryFilter: ri!addingTaskCategory,
            taskRefIdsToHide: ri!tasksToCreate.taskRefId,
            triggerRefresh: ri!triggerRefresh,
            searchText: local!searchText,
            configurationLevelCode: cons!AS_GSS_TMG_ENUM_TASK_CONFIGURATION_LEVEL_CODE_PROCESS_SETUP
          )
        }
      ),
      /*Sub-header and +Add Custom Task*/
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: rule!AS_CO_DSP_displayHeader(
              headerText: "Custom Tasks",
              headerLevel: cons!AS_CO_ENUM_HEADER_LEVEL_TWO
            )
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: #"SYSTEM_SYSRULES_ButtonLayout"(
              primaryButtons: rule!AS_CO_BTN_toolbarButton(
                label: "Add Custom Task",
                saveInto: {
                  a!save(
                    local!newCustomTask.taskCategory,
                    ri!addingTaskCategory
                  )
                },
                icon: "PLUS"
              )
            )
          )
        }
      ),
      /*Custom task grid*/
      rule!AS_GSS_TMG_GRD_runtimeTaskGrid_ProcessSetup(
        i18nData: ri!i18nData,
        staticTaskList: local!customTasks,
        allowDeletion: true
      ),
      /*Cancel/Save buttons*/
      a!localVariables(
        local!bothButtonSaves: a!refreshVariable(
          value: {
            rule!AS_CO_UT_saveNull(valueToNull: ri!addingTaskCategory),
            rule!AS_CO_UT_saveNull(valueToNull: local!customTasks),
            rule!AS_CO_UT_saveNull(valueToNull: local!selectedTaskRefs)
          },
          refreshAlways: true
        ),
        rule!AS_CO_CPS_cancelSaveButtons(
          confirmLabel: "Add Tasks",
          cancelLabel: "Cancel",
          confirmSaveInto: {
            a!save(
              target: ri!tasksToCreate,
              value: append(
                ri!tasksToCreate,
                a!flatten(
                  {
                    local!customTasks,
                    #"SYSTEM_SYSRULES_forEach"(
                      items: local!selectedTaskRefs,
                      expression: rule!AS_GSS_TMG_CDT_mapTaskRefToRuntimeTask(taskRef: fv!item, calculateDueDate: true)
                    )
                  }
                )
              )
            ),
            local!bothButtonSaves,
            a!save(
              ri!tasksToCreate[intersection(
                wherecontains(
                  null, touniformstring(ri!tasksToCreate.userAssignee)
                ), wherecontains(
                  tointeger(cons!AS_GAM_GRP_CONTRACTING_OFFICER), tointeger(
                    index(ri!tasksToCreate, "groupAssignee", {})
                  )
                )
              )].userAssignee,
              ri!evaluation.contractingOfficer.username
            ),
            a!save(
              ri!tasksToCreate[intersection(
                wherecontains(
                  null, touniformstring(ri!tasksToCreate.userAssignee)
                ), wherecontains(
                  tointeger(cons!AS_GAM_GRP_CONTRACT_SPECIALIST), tointeger(
                    index(ri!tasksToCreate, "groupAssignee", {})
                  )
                )
              )].userAssignee,
              ri!evaluation.contractingSpecialist.username
            ),
            a!save(
              ri!tasksToCreate[intersection(
                wherecontains(
                  null, touniformstring(ri!tasksToCreate.userAssignee)
                ), wherecontains(
                  tointeger(cons!AS_GSS_GRP_EVALUATION_CHAIRS), tointeger(
                    index(ri!tasksToCreate, "groupAssignee", {})
                  )
                )
              )].userAssignee,
              ri!evaluation.evaluationChief.username
            )
          },
          cancelSaveInto: { local!bothButtonSaves },
          includeDivider: true
        )
      )
    },
    /*Display when adding a new custom task*/
    rule!AS_GSS_TMG_CPS_addCustomTask(
      task: local!newCustomTask,
      i18nData: ri!i18nData,
      allowCategoryEdit: false,
      confirmSaves: {
        a!save(
          local!customTasks,
          append(local!customTasks, local!newCustomTask)
        ),
        rule!AS_CO_UT_saveNull(valueToNull: local!newCustomTask)
      },
      cancelSaves: {
        rule!AS_CO_UT_saveNull(valueToNull: local!newCustomTask)
      },
      headerLevel: cons!AS_CO_ENUM_HEADER_LEVEL_ONE,
      evaluation: ri!evaluation
    )
  )
)

a!localVariables(
  /*Return the indices of the tasks that pertain to this category*/
  local!taskIndicesForCategory: rule!AS_CO_UT_returnCdtIndicesByField(
    cdt: ri!tasksToCreate,
    field: "taskCategory.categoryName",
    value: ri!taskCategory.categoryName
  ),
  #"SYSTEM_SYSRULES_gridLayout"(
    emptyGridMessage: "No tasks available",
    headerCells: {
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: ""),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Task Name"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Task Type"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Description"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Precedents",
        helpTooltip: "Tasks without precedents will be kicked off upon submission"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Assigned Group" & " * "
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Individual Assignee"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Days from Start" & " * "
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Due Date" & " * "
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "")
    },
    columnConfigs: {
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "ICON"),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
      /*Make description and precedents fields larger*/
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 2),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 2),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "ICON")
    },
    rows: {
      #"SYSTEM_SYSRULES_forEach"(
        /*Looping over the indices so that we can know which index we are at*/
        items: local!taskIndicesForCategory,
        expression: {
          #"SYSTEM_SYSRULES_gridRowLayout"(
            contents: {
              /*index*/
              rule!AS_CO_CP_integerField(
                readOnly: true,
                value: fv!index,
                align: "CENTER"
              ),
              /*name*/
              rule!AS_CO_INP_textField(
                required: true,
                readOnly: true,
                value: ri!tasksToCreate[fv!item].taskName
              ),
              /*type*/
              rule!AS_CO_INP_textField(
                required: true,
                readOnly: true,
                value: rule!AS_CO_UT_displayRefDataLabelSingle(
                  singleData: ri!tasksToCreate[fv!item].taskBehaviorType,
                  labelField: "behaviorDisplayName",
                  i18nData: ri!i18nData
                )
              ),
              /*description*/
              rule!AS_CO_INP_textField(
                value: ri!tasksToCreate[fv!item].taskDesc,
                validations: rule!AS_CO_VD_textValidation(
                  value: ri!tasksToCreate[fv!item].taskDesc,
                  maxLength: cons!AS_CO_ENUM_TEXT_LENGTH_LONG
                ),
                saveInto: ri!tasksToCreate[fv!item].taskDesc
              ),
              /*precedents*/
              a!localVariables(
                /*value stored in taskId_precedent is not the id, but the index of the task in the list of all tasks */
                local!precTaskIndices: remove(
                  enumerate(count(ri!tasksToCreate)) + 1,
                  fv!item
                ),
                local!precTaskOptions: #"SYSTEM_SYSRULES_forEach"(
                  items: local!precTaskIndices,
                  expression: {
                    value: tointeger(fv!item),
                    label: ri!tasksToCreate[fv!item].taskName
                  }
                ),
                rule!AS_CO_CP_multipleDropdownField(
                  placeholderLabel: "Select Precedents",
                  /*Index to prevent breaking if there are no available precedents to select*/
                  choiceLabels: index(local!precTaskOptions, "label", {}),
                  choiceValues: index(local!precTaskOptions, "value", {}),
                  value: a!flatten(
                    ri!tasksToCreate[fv!item].taskPrecedents
                  ).taskId_precedent,
                  saveInto: {
                    a!save(
                      ri!tasksToCreate[fv!item].taskPrecedents,
                      #"SYSTEM_SYSRULES_forEach"(
                        items: save!value,
                        expression: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_Task_Precedent'(taskId_precedent: fv!item)
                      )
                    ),
                    a!save(ri!tasksToCreate[fv!item].dueDate, null)
                  }
                )
              ),
              /*default assignee*/
              rule!AS_CO_CP_pickerFieldGroups(
                required: true,
                maxSelections: 1,
                groupFilter: cons!AS_GSS_TMG_GRP_TASK_RECIPIENTS,
                value: ri!tasksToCreate[fv!item].groupAssignee,
                saveInto: {
                  ri!tasksToCreate[fv!item].groupAssignee,
                  a!save(
                    ri!tasksToCreate[fv!item].userAssignee,
                    if(
                      ri!tasksToCreate[fv!item].groupAssignee = tointeger(cons!AS_GAM_GRP_CONTRACTING_OFFICER),
                      ri!evaluation.contractingOfficer.username,
                      if(
                        ri!tasksToCreate[fv!item].groupAssignee = tointeger(cons!AS_GAM_GRP_CONTRACT_SPECIALIST),
                        ri!evaluation.contractingSpecialist.username,
                        if(
                          ri!tasksToCreate[fv!item].groupAssignee = tointeger(cons!AS_GSS_GRP_EVALUATION_CHAIRS),
                          ri!evaluation.evaluationChief.username,
                          null
                        )
                      )
                    )
                  )
                }
              ),
              /*individual assignee*/
              rule!AS_CO_CP_pickerFieldUsers(
                maxSelections: 1,
                groupFilter: ri!tasksToCreate[fv!item].groupAssignee,
                value: ri!tasksToCreate[fv!item].userAssignee,
                saveInto: ri!tasksToCreate[fv!item].userAssignee,
                placeholder: "User(s)",
                disabled: rule!AS_CO_UT_isBlank(
                  ri!tasksToCreate[fv!item].groupAssignee
                )
              ),
              /*Days From Start*/
              rule!AS_CO_CP_integerField(
                required: rule!AS_CO_UT_isNotBlank(
                  ri!tasksToCreate[fv!item].taskPrecedents
                ),
                disabled: rule!AS_CO_UT_isBlank(
                  ri!tasksToCreate[fv!item].taskPrecedents
                ),
                value: ri!tasksToCreate[fv!item].noOfDaysFromStart,
                saveInto: {
                  ri!tasksToCreate[fv!item].noOfDaysFromStart,
                  a!save(ri!tasksToCreate[fv!item].dueDate, null)
                },
                validations: rule!AS_CO_VD_numberValidation(
                  value: ri!tasksToCreate[fv!item].noOfDaysFromStart,
                  minSize: 1,
                  minSizeMessage: "Please enter a value greater than 0",
                  maxSize: cons!AS_GSS_TMG_TEXT_LENGTH_FOR_DAYS_FROM_START,
                  maxSizeMessage: "Please enter a value less than" & " " & cons!AS_GSS_TMG_TEXT_LENGTH_FOR_DAYS_FROM_START
                )
              ),
              /*due date*/
              rule!AS_CO_INP_dateField(
                required: rule!AS_CO_UT_isBlank(
                  ri!tasksToCreate[fv!item].taskPrecedents
                ),
                disabled: rule!AS_CO_UT_isNotBlank(
                  ri!tasksToCreate[fv!item].taskPrecedents
                ),
                value: ri!tasksToCreate[fv!item].dueDate,
                validations: if(
                  ri!tasksToCreate[fv!item].dueDate < today(),
                  "Enter a future date",
                  ""
                ),
                saveInto: ri!tasksToCreate[fv!item].dueDate
              ),
              /*remove task*/
              rule!AS_CO_DSP_removeIcon(
                caption: "Remove Task",
                saveInto: {
                  rule!AS_GSS_TMG_UT_removeTaskFromProcessDuringSetupSaves(
                    tasksToCreate: ri!tasksToCreate,
                    taskIndexToRemove: fv!item
                  )
                },
                removalType: cons!AS_CO_ENUM_REMOVAL_ICON_TYPE_CLEAR
              )
            }
          )
        }
      )
    }
  )
)

