        groups: cons!AS_GSS_GRP_EDIT_DUE_DATE_RA_ACCESS,
        user: ri!user
      ),

    ),
    rule!AS_CO_UT_isBlank(ri!selectedTasks),
    tointeger(
      ri!selectedTasks[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.evaluationStatus.refDataId]
    ) <> cons!AS_GSS_REF_ID_EVALUATION_STATUS_INPROGRESS
  ),
  local!isMarkCompletedDisabled: rule!AS_GSS_TMG_isMarkCompletedDisabled(
    selectedTasks: ri!selectedTasks,
    user: ri!user
  ),
  local!isMarkNotNeededDisabled: rule!AS_GSS_TMG_isMarkNotNeededDisabled(
    selectedTasks: ri!selectedTasks,
    user: ri!user
  ),
  local!isClaimTaskDisabled: rule!AS_GSS_TMG_isClaimTaskDisabled(
