          item: rule!AS_GSS_DSP_recordActionsMarkComplete(
            i18nData: ri!i18nData,
            selectedTasks: ri!selectedTasks,
            isMarkCompleteDisabled: local!isMarkCompletedDisabled
          ),
          width: "MINIMIZE"
        ),
        /*Mark Not Needed*/
