          }
        )
      }
    ),
    local!updatedEvaluationRecord: rule!AS_GSS_UT_updateEvaluationRecordWithAllTasks(
      evaluation: local!evaluation,
      triggerRefresh: ri!triggerRefresh,
      executeWhen: true(),
      taskFields: {
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskStatusId
      },
      additionalFilters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee,
          operator: "=",
          value: ri!loggedInUser
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskStatusId,
          operator: "in",
          value: {
            cons!AS_GSS_TMG_REF_ID_TASK_STATUS_QUEUED,
            cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
            cons!AS_GSS_TMG_REF_ID_TASK_STATUS_IN_PROGRESS,
            cons!AS_GSS_TMG_REF_ID_TASK_STATUS_COMPLETE
          }
        )
      }
    ),
    local!taskDictionary: if(
      ri!isEvaluator,
      /*Evaluator Task Progess (self assigned)*/
      a!map(
        totalRequiredTasks: rule!AS_GAM_UT_ArrayLength(
          array: rule!AS_CO_UT_filterRecordByFieldValues(
            records: local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.task],
            field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskStatusId,
            values: {
              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_QUEUED,
              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_IN_PROGRESS,
              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_COMPLETE
            }
          )
        ),
        totalCompletedTasks: rule!AS_GAM_UT_ArrayLength(
          array: rule!AS_CO_UT_filterRecordByFieldValues(
            records: local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.task],
            field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskStatusId,
            values: cons!AS_GSS_TMG_REF_ID_TASK_STATUS_COMPLETE
          )
        )
      ),
      if(
        local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStatusId] = cons!AS_GSS_REF_ID_EVALUATION_STATUS_SETTING_UP,
        /*Evaluation Setup Progress for Evaluations in Setting Up stage*/
        a!map(
          totalRequiredTasks: 100,
          totalCompletedTasks: rule!AS_GSS_BL_computeEvaluationProgress(
            evaluation: local!updatedEvaluationRecord
          )
        ),
        /*Evaluation Task Progress for all tasks in an evaluation*/
        a!map(
          totalRequiredTasks: local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.totalTaskCount],
          totalCompletedTasks: local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.completedTaskCount]
        )
      )
    ),
    local!progressPercentage: rule!AS_GSS_TMG_UT_calculateTaskPercentCompletion(taskDictionary: local!taskDictionary),
    a!map(
      evaluationId: local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId],
      evaluationTitle: local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationTitle],
      evaluationNumber: local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationNumber],
      evaluationDueDate: local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationDueDate],
      evaluationStatus: local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStatus.refLabel],
      evaluationStatusId: local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStatusId],
      progressPercentage: local!progressPercentage,
      taskDictionary: local!taskDictionary,
      evaluators: rule!AS_CO_UT_distinct(
        value: touniformstring(
          local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.criteria.CriteriaAssignment.assignee]
        )
      ),
      teams: rule!AS_CO_UT_rejectNullValuesFromArray(
        array: rule!AS_CO_UT_distinct(
          value: #"SYSTEM_SYSRULES_forEach"(
            items: local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.team],
            expression: if(
              a!isNullOrEmpty(
                value: fv!item[recordType!AS_GSS_EvaluatorTeam_SYNCEDRECORD.teamMembership]
              ),
              {},
              fv!item[recordType!AS_GSS_EvaluatorTeam_SYNCEDRECORD.teamName]
            )
          )
        )
      ),
      evaluatorsCount: length(
        rule!AS_CO_UT_replaceNull(
          nullableValue: union(
            touniformstring(
              local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.criteria.CriteriaAssignment.assignee]
            ),
            touniformstring(
              local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.criteria.CriteriaAssignment.assignee]
            )
          ),
          replacementValue: {}
        )
      ),
      isEvaluatorMasked: a!defaultValue(
        local!updatedEvaluationRecord[recordType!AS_GSS_Evaluation_SYNCEDRECORD.isEvaluatorMasked],
        false()
      )
    )
  )
)

