/*
  Should return true if:
    1) All criteria are completed, and
    2) Evaluation should be in in progress status and
    3) User is a member of CO, CS, EC, or CM

*/
a!localVariables(
  local!incompleteCriteria: rule!AS_GSS_QE_getEvaluationCriteria(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    evaluationId: ri!evaluationId,
    getActiveAndInactive: false,
    additionalFilters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: "criteriaStatus.refDataId",
        operator: "<>",
        value: cons!AS_GSS_REF_ID_CRITERIA_STATUS_COMPLETED,

      )
    }
  ),
  local!evaluation: rule!AS_GSS_QE_getEvaluation(
    evaluationId: ri!evaluationId,
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT
  ),
  local!getLptaTask: rule!AS_GSS_TMG_QR_getTasks(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    evaluationId: ri!evaluationId,
    additionalFilters: #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskRefId,
      operator: "=",
      value: cons!AS_GSS_TMG_TASK_REF_ID_COMPLETE_LPTA_TASK
    )
  ),
  and(
    if(
      local!evaluation.evaluationMethod.refDataId = cons!AS_GSS_REF_ID_EVALUATION_METHOD_LPTA,
      tointeger(
        local!getLptaTask[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskStatusId]
      ) = cons!AS_GSS_TMG_REF_ID_TASK_STATUS_COMPLETE,
      rule!AS_CO_UT_isBlank(local!incompleteCriteria)
    ),
    local!evaluation.evaluationStatus.refDataId = cons!AS_GSS_REF_ID_EVALUATION_STATUS_INPROGRESS,
    rule!AS_CO_UT_isUserMemberOfGroups(
      groups: {
        cons!AS_GSS_GRP_EVALUATION_CHAIRS,
        cons!AS_GAM_GRP_CONTRACT_SPECIALIST,
        cons!AS_GAM_GRP_CONTRACTING_OFFICER,
        cons!AS_GAM_GRP_CONTRACTING_MANAGER
      },
      user: loggedInUser()
    )
  )
)

