a!localVariables(
  /*The button layout, which can dynamically be inside a section or not*/
  /*Note that when using this pattern of storing an interface as a local variable, it must be set to refreshAlways = true*/
  local!buttonLayout: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_ButtonLayout"(
      primaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          accessibilityText: ri!confirmAccessibilityText,
          label: ri!confirmLabel,
          size: ri!confirmSize,
          saveInto: if(
            rule!AS_CO_UT_isBlank(
              ri!confirmSaveInto
            ),
            {},
            ri!confirmSaveInto
          ),
          style: rule!AS_CO_UT_replaceNull(
            nullableValue: ri!confirmStyle,
            replacementValue: "PRIMARY"
          ),
          validate: true,
          validationGroup: ri!confirmValidationGroup,
          submit: ri!confirmSubmit,
          disabled: ri!confirmDisabled,
          confirmHeader: ri!confirmHeader,
          confirmMessage: ri!confirmMessage
        )
      },
      secondaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          accessibilityText: ri!cancelAccessibilityText,
          label: ri!cancelLabel,
          size: ri!cancelSize,
          saveInto: if(
            rule!AS_CO_UT_isBlank(
              ri!cancelSaveInto
            ),
            {},
            ri!cancelSaveInto
          ),
          style: rule!AS_CO_UT_replaceNull(
            nullableValue: ri!cancelStyle,
            replacementValue: "NORMAL"
          ),
          validate: false,
          submit: ri!cancelSubmit,
          disabled: ri!cancelDisabled,
          confirmHeader: ri!cancelHeader,
          confirmMessage: ri!cancelMessage,
          showWhen: ri!cancelShowWhen
        )
      },
      showWhen: ri!showWhen
    ),
    refreshAlways: true
  ),
  /*Components to show*/
  if(
    ri!includeDivider,
    #"SYSTEM_SYSRULES_sectionLayout_v1"(
      divider: "ABOVE",
      contents: {
        local!buttonLayout
      }
    ),
    local!buttonLayout
  )
)

a!localVariables(
  local!queryAggResults: rule!AS_CO_UT_queryEntity(
    entity: cons!AS_GSS_TMG_ENT_TASK,
    entityType: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_Task',
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_AGGREGATION,
    aggregation: #"SYSTEM_SYSRULES_queryAggregation"(
      aggregationColumns: {
        #"SYSTEM_SYSRULES_queryAggregationColumn"(
          field: "criteria.criteriaId",
          alias: "criteriaId",
          isGrouping: true
        ),
        #"SYSTEM_SYSRULES_queryAggregationColumn"(
          field: "taskStatus.taskStatusId",
          alias: "taskStatusId",
          isGrouping: true
        ),
        /* Don't really need COUNT but included it for fun */
        #"SYSTEM_SYSRULES_queryAggregationColumn"(
          field: "taskStatus.statusDisplayName",
          alias: "taskStatusCount",
          aggregationFunction: "COUNT"
        )
      }
    ),
    logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
      operator: "AND",
      filters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: "evaluation.evaluationId",
          operator: "=",
          value: ri!evaluation.evaluationId
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: "taskRefId",
          operator: "=",
          value: cons!AS_GSS_TMG_TASK_REF_ID_COMPLETE_EVALUATION
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: "taskStatus.taskStatusId",
          operator: "not in",
          value: {
            cons!AS_GSS_TMG_REF_ID_TASK_STATUS_NOT_NEEDED,
            cons!AS_GSS_TMG_REF_ID_TASK_STATUS_CANCELLED,
            cons!AS_GSS_TMG_REF_ID_TASK_STATUS_DELETED
          }
        )
      }
    )
  ),
  local!uniqueCriteria: union(
    local!queryAggResults.criteriaId,
    local!queryAggResults.criteriaId
  ),
  local!completedCriteriaIds: rule!AS_CO_UT_rejectNullValuesFromArray(
    #"SYSTEM_SYSRULES_forEach"(
      items: local!uniqueCriteria,
      expression: a!localVariables(
        local!filteredResults: rule!AS_CO_UT_filterCdtByField(
          cdt: local!queryAggResults,
          field: "criteriaId",
          value: fv!item
        ),
        if(
          and(
            count(local!filteredResults) = 1,
            local!filteredResults.taskStatusId = cons!AS_GSS_TMG_REF_ID_TASK_STATUS_COMPLETE
          ),
          fv!item,
          null
        )
      )
    )
  ),
  local!criteriaData: rule!AS_GSS_QE_getEvaluationCriteria(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    evaluationId: ri!evaluation.evaluationId,
    getActiveAndInactive: false,
    additionalFilters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: "criteriaStatus.refDataId",
        operator: "<>",
        value: cons!AS_GSS_REF_ID_CRITERIA_STATUS_COMPLETED,

      ),
      #"SYSTEM_SYSRULES_queryFilter"(
        field: "criteriaId",
        operator: "in",
        value: tointeger(local!completedCriteriaIds)
      )
    }
  ),
  local!criteriaDataSorted: rule!AS_GSS_orderCriteriaByParentAndChildren(criterias: local!criteriaData),
  local!selection,
  local!selectedData,
  #"SYSTEM_SYSRULES_gridLayout"(
    labelPosition: "COLLAPSED",
    headerCells: {
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Number|Name"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Evaluation Team"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Evaluators"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Rating Type"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Due Date"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Completion Date *"
      )
    },
    rows: #"SYSTEM_SYSRULES_forEach"(
      items: local!criteriaDataSorted,
      expression: {
        #"SYSTEM_SYSRULES_gridRowLayout"(
          id: fv!index,
          contents: {
            /* Number/Name */
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: fv!item.factorNumber & "|" & fv!item.criteriaName
              )
            ),
            /* Evaluation Team */
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              value: #"SYSTEM_SYSRULES_richTextItem_v1"(text: fv!item.evaluatorTeam.teamName),

            ),
            /* Evaluators */
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              value: #"SYSTEM_SYSRULES_richTextBulletedList"(
                items: #"SYSTEM_SYSRULES_forEach"(
                  items: rule!AS_CO_UT_filterCdtByField(
                    cdt: fv!item.assignments,
                    field: "isActive",
                    value: true
                  ),
                  expression: rule!AS_GAM_UT_concatUserFirstNameLastName(
                    firstName: fv!item.assignee.firstName,
                    lastName: fv!item.assignee.lastName
                  )
                )
              )
            ),
            /* Rating */
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              value: #"SYSTEM_SYSRULES_richTextItem_v1"(text: fv!item.ratingType.label)
            ),
            /* Due Date */
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              value: #"SYSTEM_SYSRULES_richTextItem_v1"(text: fv!item.dueDate)
            ),
            /* Completion Date */
            rule!AS_CO_INP_dateField(
              required: if(
                rule!AS_CO_UT_isBlank(local!selectedData),
                false,
                contains(
                  { local!selectedData.criteriaId },
                  fv!item.criteriaId
                ),

              ),
              disabled: not(
                if(
                  rule!AS_CO_UT_isBlank(local!selectedData),
                  false,
                  contains(
                    { local!selectedData.criteriaId },
                    fv!item.criteriaId
                  ),

                ),

              ),
              value: fv!item.completedOn,
              saveInto: {
                fv!item.completedOn,
                a!save(
                  ri!completedCriteria[wherecontains(
                    fv!item.criteriaId, ri!completedCriteria.criteriaId
                  )],
                  fv!item
                )
              },
              validations: if(
                and(
                  if(
                    rule!AS_CO_UT_isBlank(local!selectedData),
                    false,
                    contains(
                      { local!selectedData.criteriaId },
                      fv!item.criteriaId
                    ),

                  ),
                  fv!item.completedOn > today()
                ),
                "Date cannot be in the future",
                {}
              )
            )
          }
        )
      }
    ),
    selectable: true,
    selectionRequired: true,
    rowHeader: 1,
    selectionValue: local!selection,
    selectionSaveInto: {
      a!save(local!selection, a!flatten(save!value)),
      a!save(
        local!selectedData,
        if(
          rule!AS_CO_UT_isBlank(local!selection),
          {},
          local!criteriaDataSorted[local!selection]
        )
      ),
      a!save(
        ri!completedCriteria,
        if(
          rule!AS_CO_UT_isBlank(local!selection),
          {},
          local!criteriaDataSorted[local!selection]
        )
      )
    },
    borderStyle: "LIGHT"
  )
)

