data.sail_code:
/*
  Should return true if:
    1) There are any criteria that have all of the Complete Evaluation tasks completed, and
    2) User is a member of CO, CS, EC, FC, FA

*/
and(
  a!localVariables(
    local!queryAggResults: rule!AS_CO_UT_queryEntity(
      entity: cons!AS_GSS_TMG_ENT_TASK,
      entityType: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_Task',
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_AGGREGATION,
      aggregation: #"SYSTEM_SYSRULES_queryAggregation"(
        aggregationColumns: {
          #"SYSTEM_SYSRULES_queryAggregationColumn"(
            field: "criteria.criteriaId",
            alias: "criteriaId",
            isGrouping: true
          ),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(
            field: "taskStatus.taskStatusId",
            alias: "taskStatusId",
            isGrouping: true
          ),
          /* Don't really need COUNT but included it for fun */
          #"SYSTEM_SYSRULES_queryAggregationColumn"(
            field: "taskStatus.statusDisplayName",
            alias: "taskStatusCount",
            aggregationFunction: "COUNT"
          )
        }
      ),
      logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "evaluation.evaluationId",
            operator: "=",
            value: ri!evaluation[recordType!AS_GSS_Evaluation_RECORD.evaluationId]
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "taskRefId",
            operator: "=",
            value: cons!AS_GSS_TMG_TASK_REF_ID_COMPLETE_EVALUATION
          )
        }
      )
    ),
    local!uniqueCriteria: union(
      local!queryAggResults.criteriaId,
      local!queryAggResults.criteriaId
    ),
    local!completedCriteriaIds: rule!AS_CO_UT_rejectNullValuesFromArray(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!uniqueCriteria,
        expression: a!localVariables(
          local!filteredResults: rule!AS_CO_UT_filterCdtByField(
            cdt: local!queryAggResults,
            field: "criteriaId",
            value: fv!item
          ),
          if(
            contains(
              {
                cons!AS_GSS_TMG_REF_ID_TASK_STATUS_COMPLETE,
                cons!AS_GSS_TMG_REF_ID_TASK_STATUS_NOT_NEEDED,
                cons!AS_GSS_TMG_REF_ID_TASK_STATUS_CANCELLED
              },
              local!filteredResults.taskStatusId
            ),
            fv!item,
            null
          )
        )
      )
    ),
    local!criteriaData: if(
      and(
        rule!AS_CO_UT_isBlank(value: local!completedCriteriaIds),
        ri!evaluation[recordType!AS_GSS_Evaluation_RECORD.isOnSpotConsensus] = false(),
        not(
          tointeger(
            ri!evaluation[recordType!AS_GSS_Evaluation_RECORD.evaluationMethod.refDataId]
          ) = cons!AS_GSS_REF_ID_EVALUATION_METHOD_LPTA
        )
      ),
      null,
      rule!AS_GSS_QE_getEvaluationCriteria(
        returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
        evaluationId: ri!evaluation[recordType!AS_GSS_Evaluation_RECORD.evaluationId],
        getActiveAndInactive: false,
        additionalFilters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "criteriaStatus.refDataId",
            operator: "<>",
            value: cons!AS_GSS_REF_ID_CRITERIA_STATUS_COMPLETED
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "criteriaId",
            operator: "in",
            value: tointeger(local!completedCriteriaIds),
            applyWhen: a!isNotNullOrEmpty(value: local!completedCriteriaIds)
          )
        }
      )
    ),
    and(
      rule!AS_CO_UT_isNotBlank(input: local!criteriaData),
      ri!evaluation[recordType!AS_GSS_Evaluation_RECORD.evaluationStatus.refDataId] = cons!AS_GSS_REF_ID_EVALUATION_STATUS_INPROGRESS
    )
  ),
  rule!AS_CO_UT_isUserMemberOfGroups(
    groups: {
      cons!AS_GSS_GRP_EVALUATION_CHAIRS,
      cons!AS_GAM_GRP_CONTRACT_SPECIALIST,
      cons!AS_GAM_GRP_CONTRACTING_OFFICER,
      cons!AS_GSS_GRP_CRITERIA_CHAIRS,
      cons!AS_GSS_GRP_FACTOR_ADVISOR
    },
